from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    if N == 0:
        return {}
    threshold = math.ceil(minsup * N)
    if threshold == 0:
        threshold = 1
    transactions_sets = [set(t) for t in transactions]
    all_items = set()
    for t in transactions:
        all_items.update(t)
    all_items = sorted(all_items)
    result = {}
    current_candidates = [tuple([item]) for item in all_items]
    while current_candidates:
        candidate_support = {}
        for candidate in current_candidates:
            count = 0
            for t_set in transactions_sets:
                if all((item in t_set for item in candidate)):
                    count += 1
            if count >= threshold:
                candidate_support[candidate] = count
                result[candidate] = count
        if not candidate_support:
            break
        next_candidates = set()
        k = len(current_candidates[0])
        for c1 in candidate_support:
            for c2 in candidate_support:
                if c1 < c2:
                    if c1[:-1] == c2[:-1]:
                        new_candidate = tuple(sorted(c1 + c2))
                        next_candidates.add(new_candidate)
        current_candidates = list(next_candidates)
    return result

import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if isinstance(X, list):
        if not all((isinstance(row, list) for row in X)):
            return False
        if not X:
            return False
        row_length = len(X[0])
        for row in X:
            if len(row) != row_length:
                return False
        X_np = np.array(X)
    else:
        if X.ndim != 2:
            return False
        X_np = X
    if not ((X_np >= 0).all() and (X_np <= 1).all()):
        return False
    row_sums = np.sum(X_np, axis=1)
    if not np.allclose(row_sums, 1, atol=1e-08):
        return False
    return True

import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative."""
    x = np.asarray(x)
    original_scalar = x.ndim == 0
    if original_scalar:
        x = x.reshape(1)
    if deriv:
        y = 1 / (1 + np.exp(-x))
    else:
        y = np.where(x > 20, x, np.log1p(np.exp(x)))
    y_rounded = np.round(y, 4)
    if original_scalar:
        return float(y_rounded[0])
    else:
        return y_rounded.tolist()

import math
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    if N == 1:
        total_tokens = len(corpus)
        if total_tokens == 0:
            if len(sequence) > 0:
                return float('-inf')
            else:
                return 0.0
        ngram_counts = Counter(corpus)
        sum_log_prob = 0.0
        for word in sequence:
            count = ngram_counts.get(word, 0)
            if count == 0:
                return float('-inf')
            prob = count / total_tokens
            sum_log_prob += math.log(prob)
    else:
        ngram_counts = Counter()
        ngram_minus_1_counts = Counter()
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            ngram_counts[ngram] += 1
            prefix = tuple(corpus[i:i + N - 1])
            ngram_minus_1_counts[prefix] += 1
        sum_log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            current_ngram = tuple(sequence[i:i + N])
            count_ngram = ngram_counts.get(current_ngram, 0)
            if count_ngram == 0:
                return float('-inf')
            prefix = tuple(sequence[i:i + N - 1])
            count_prefix = ngram_minus_1_counts.get(prefix, 0)
            if count_prefix == 0:
                return float('-inf')
            prob = count_ngram / count_prefix
            sum_log_prob += math.log(prob)
    return round(sum_log_prob, 4)

def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not observations:
        return -1
    n = len(S)
    if len(S) != n:
        return -1
    if len(A) != n:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    if len(B) != n:
        return -1
    if not B:
        return -1
    m = len(B[0])
    for row in B:
        if len(row) != m:
            return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    T = len(observations)
    alpha = [0.0] * n
    k0 = observations[0]
    for i in range(n):
        alpha[i] = S[i] * B[i][k0]
    for t in range(1, T):
        kt = observations[t]
        new_alpha = [0.0] * n
        for i in range(n):
            sum_val = 0.0
            for j in range(n):
                sum_val += alpha[j] * A[j][i]
            new_alpha[i] = sum_val * B[i][kt]
        alpha = new_alpha
    result = sum(alpha)
    return round(result, 4)

