from collections import Counter
def additive_ngram_log_prob(corpus: list[str], sequence: str, N: int, K: float=1.0) -> float:
    """Compute the additive-smoothed log-probability of *sequence* given a corpus."""
    ngram_counts = {n: Counter() for n in range(1, N + 1)}
    for sentence in corpus:
        words = sentence.split()
        for n in range(1, N + 1):
            if len(words) < n:
                continue
            for i in range(len(words) - n + 1):
                ngram = tuple(words[i:i + n])
                ngram_counts[n][ngram] += 1
    vocabulary = set()
    for sentence in corpus:
        words = sentence.split()
        for word in words:
            vocabulary.add(word)
    vocabulary.add('<UNK>')
    vocabulary_size = len(vocabulary)
    target_words = sequence.split()
    processed_target = []
    for word in target_words:
        if word in vocabulary:
            processed_target.append(word)
        else:
            processed_target.append('<UNK>')
    if len(processed_target) < N:
        return 0.0
    total_log_prob = 0.0
    num_sentences = len(corpus)
    for i in range(len(processed_target) - N + 1):
        ngram = tuple(processed_target[i:i + N])
        if N == 1:
            context = ()
            count_context = num_sentences
        else:
            context = ngram[:-1]
            count_context = ngram_counts[N - 1].get(context, 0)
        count_context_plus_word = ngram_counts[N].get(ngram, 0)
        denominator = count_context + K * vocabulary_size
        numerator = count_context_plus_word + K
        if denominator == 0:
            prob = 0.0
        else:
            prob = numerator / denominator
        if prob <= 0:
            log_prob = float('-inf')
        else:
            log_prob = math.log(prob)
        total_log_prob += log_prob
    return round(total_log_prob, 4)

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Converts a text string into a fixed-length list of integer token ids."""
    encoded = [vocabulary.get(c, 0) for c in text]
    if len(encoded) < time_steps:
        encoded += [0] * (time_steps - len(encoded))
    else:
        encoded = encoded[:time_steps]
    return encoded
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to text, ignoring padding tokens (0)."""
    filtered = []
    for idx in indices:
        if idx != 0:
            char = inverse_vocab.get(idx, '')
            filtered.append(char)
    return ''.join(filtered)
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Runs the model on encoded text and decodes the prediction."""
    encoded = string_to_int(text, TIME_STEPS, input_vocabulary)
    encoded_np = np.array(encoded, dtype=np.int32).reshape(1, -1)
    predictions = model.predict(encoded_np)
    predicted_ids = np.argmax(predictions, axis=-1).flatten()
    decoded = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Implement AdaBoost with decision stumps as weak learners."""
    y_train = [1 if label == 1 else -1 for label in y_train]
    m = len(X_train)
    if m == 0:
        return []
    weights = np.ones(m) / m
    stumps = []
    for round in range(n_estimators):
        best_error = 1.0
        best_stump = None
        for j in range(len(X_train[0])):
            feature_data = []
            for i in range(m):
                x = X_train[i][j]
                y = y_train[i]
                w = weights[i]
                feature_data.append((x, y, w))
            feature_data.sort(key=lambda x: x[0])
            x_list = [fd[0] for fd in feature_data]
            y_list = [fd[1] for fd in feature_data]
            w_list = [fd[2] for fd in feature_data]
            prefix_1 = []
            prefix_neg1 = []
            sum_1 = 0.0
            sum_neg1 = 0.0
            for i in range(len(feature_data)):
                if y_list[i] == 1:
                    sum_1 += w_list[i]
                else:
                    sum_neg1 += w_list[i]
                prefix_1.append(sum_1)
                prefix_neg1.append(sum_neg1)
            total_1 = sum_1
            total_neg1 = sum_neg1
            min_error_p1 = 1.0
            best_t_p1 = None
            for i in range(len(x_list)):
                t = x_list[i]
                sum_neg1_less = prefix_neg1[i]
                sum_1_ge = total_1 - prefix_1[i]
                error = sum_neg1_less + sum_1_ge
                if error < min_error_p1:
                    min_error_p1 = error
                    best_t_p1 = t
            min_error_p_neg1 = 1.0
            best_t_p_neg1 = None
            for i in range(len(x_list)):
                t = x_list[i]
                sum_1_less = prefix_1[i]
                sum_neg1_ge = total_neg1 - prefix_neg1[i]
                error = sum_1_less + sum_neg1_ge
                if error < min_error_p_neg1:
                    min_error_p_neg1 = error
                    best_t_p_neg1 = t
            if min_error_p1 < min_error_p_neg1:
                current_error = min_error_p1
                current_t = best_t_p1
                current_p = 1
            else:
                current_error = min_error_p_neg1
                current_t = best_t_p_neg1
                current_p = -1
            if current_error < best_error:
                best_error = current_error
                best_stump = (j, current_t, current_p)
            elif current_error == best_error:
                if j < best_stump[0]:
                    best_error = current_error
                    best_stump = (j, current_t, current_p)
                elif j == best_stump[0]:
                    if current_t < best_stump[1]:
                        best_error = current_error
                        best_stump = (j, current_t, current_p)
                    elif current_t == best_stump[1]:
                        if current_p == 1 and best_stump[2] == -1:
                            best_error = current_error
                            best_stump = (j, current_t, current_p)
        if best_stump is None:
            break
        if best_error == 0:
            alpha_t = 0.5 * np.log((1 - best_error) / (best_error + 1e-10))
        else:
            alpha_t = 0.5 * np.log((1 - best_error) / (best_error + 1e-10))
        for i in range(m):
            x = X_train[i][best_stump[0]]
            t = best_stump[1]
            p = best_stump[2]
            if x < t and p == 1 or (x >= t and p == -1):
                h = 1
            else:
                h = -1
            weights[i] *= np.exp(-alpha_t * y_train[i] * h)
        sum_weights = sum(weights)
        if sum_weights == 0:
            weights = np.ones(m) / m
        else:
            weights = [w / sum_weights for w in weights]
        stumps.append((best_stump[0], best_stump[1], best_stump[2], alpha_t))
        if best_error == 0:
            break
    predictions = []
    for x in X_test:
        sum_vote = 0.0
        for (j, t, p, alpha) in stumps:
            x_j = x[j]
            if x_j < t and p == 1 or (x_j >= t and p == -1):
                h = 1
            else:
                h = -1
            sum_vote += alpha * h
        if sum_vote >= 0:
            predictions.append(1)
        else:
            predictions.append(0)
    return predictions

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini(y):
        count_0 = np.sum(y == 0)
        count_1 = np.sum(y == 1)
        n = len(y)
        if n == 0:
            return 0.0
        p0 = count_0 / n
        p1 = count_1 / n
        return 1 - (p0 ** 2 + p1 ** 2)

    def build_tree(X, y, current_depth, max_depth):
        if len(X) == 0:
            return {'is_leaf': True, 'prediction': 0}
        if len(np.unique(y)) == 1:
            return {'is_leaf': True, 'prediction': y[0]}
        if max_depth is not None and current_depth >= max_depth:
            majority = np.bincount(y).argmax()
            return {'is_leaf': True, 'prediction': majority}
        best_gain = -1
        best_feature = None
        best_threshold = None
        best_left_X = None
        best_left_y = None
        best_right_X = None
        best_right_y = None
        n_features = X.shape[1]
        for feature in range(n_features):
            column = X[:, feature]
            sorted_col = np.sort(column)
            unique = np.unique(sorted_col)
            if len(unique) == 1:
                continue
            for i in range(len(unique) - 1):
                threshold = (unique[i] + unique[i + 1]) / 2
                left_mask = column <= threshold
                left_X = X[left_mask]
                left_y = y[left_mask]
                right_X = X[~left_mask]
                right_y = y[~left_mask]
                G_parent = gini(y)
                G_left = gini(left_y) if len(left_y) > 0 else 0
                G_right = gini(right_y) if len(right_y) > 0 else 0
                n_total = len(y)
                n_left = len(left_y)
                n_right = len(right_y)
                if n_total == 0:
                    continue
                weighted_G = n_left / n_total * G_left + n_right / n_total * G_right
                gain = G_parent - weighted_G
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
                    best_left_X = left_X
                    best_left_y = left_y
                    best_right_X = right_X
                    best_right_y = right_y
        if best_gain > 0:
            left_child = None
            right_child = None
            if len(best_left_X) == 0:
                majority = np.bincount(y).argmax()
                left_child = {'is_leaf': True, 'prediction': majority}
            else:
                left_child = build_tree(best_left_X, best_left_y, current_depth + 1, max_depth)
            if len(best_right_X) == 0:
                majority = np.bincount(y).argmax()
                right_child = {'is_leaf': True, 'prediction': majority}
            else:
                right_child = build_tree(best_right_X, best_right_y, current_depth + 1, max_depth)
            return {'is_leaf': False, 'feature': best_feature, 'threshold': best_threshold, 'left': left_child, 'right': right_child}
        else:
            majority = np.bincount(y).argmax()
            return {'is_leaf': True, 'prediction': majority}

    def predict_sample(sample, tree):
        if tree['is_leaf']:
            return tree['prediction']
        else:
            feature = tree['feature']
            threshold = tree['threshold']
            if sample[feature] <= threshold:
                return predict_sample(sample, tree['left'])
            else:
                return predict_sample(sample, tree['right'])
    tree = build_tree(X_train, y_train, 0, max_depth)
    predictions = []
    for sample in X_test:
        predictions.append(predict_sample(sample, tree))
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}
    if name not in supported:

        def d(x, y):
            return -1
        return d

    def d(x, y):
        try:
            x_arr = np.asarray(x)
            y_arr = np.asarray(y)
        except:
            return -1
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return -1
        if len(x_arr) != len(y_arr) or len(x_arr) == 0:
            return -1
        if name == 'cosine':
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            if norm_x == 0 or norm_y == 0:
                return -1
        if name == 'euclidean':
            distance = np.sqrt(np.sum((x_arr - y_arr) ** 2))
        elif name == 'manhattan':
            distance = np.sum(np.abs(x_arr - y_arr))
        elif name == 'chebyshev':
            distance = np.max(np.abs(x_arr - y_arr))
        elif name == 'cosine':
            dot = np.dot(x_arr, y_arr)
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            distance = 1 - dot / (norm_x * norm_y)
        else:
            return -1
        return round(distance, 4)
    return d

import numpy as np
from itertools import product
from typing import Any, List, Tuple, Union
def action_stats(env: Any, md_action: bool, cont_action: bool) -> Tuple[List[Union[int, float]], Union[List[Tuple[int, ...]], None], int]:
    """Summarise an RL environment's action space.

    Args:
        env: Environment instance exposing an ``action_space`` attribute mimicking OpenAI Gym.
        md_action: ``True`` if the action space is multi-dimensional.
        cont_action: ``True`` if the action space is continuous (infinite number of actions).

    Returns:
        A tuple ``(n_actions_per_dim, action_ids, action_dim)`` where
            • n_actions_per_dim: list with the number of actions in every dimension
                                  (``numpy.inf`` for continuous ones),
            • action_ids: list of all discrete actions (cartesian product) or ``None`` if
                           any dimension is continuous,
            • action_dim: number of action dimensions.
    """
    action_space = env.action_space
    if hasattr(action_space, 'spaces'):
        action_dim = len(action_space.spaces)
    elif hasattr(action_space, 'shape'):
        action_dim = len(action_space.shape)
    else:
        action_dim = 1
    n_actions_per_dim = []
    if hasattr(action_space, 'spaces'):
        for space in action_space.spaces:
            if hasattr(space, 'n'):
                n_actions_per_dim.append(space.n)
            else:
                n_actions_per_dim.append(np.inf)
    elif hasattr(action_space, 'n'):
