def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function must follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    sum_p = 0
    aces_p = 0
    for card in player:
        if card == 1:
            aces_p += 1
            sum_p += 11
        else:
            sum_p += card
    while sum_p > 21 and aces_p > 0:
        sum_p -= 10
        aces_p -= 1
    player_bust = sum_p > 21
    sum_d = 0
    aces_d = 0
    for card in dealer:
        if card == 1:
            aces_d += 1
            sum_d += 11
        else:
            sum_d += card
    while sum_d > 21 and aces_d > 0:
        sum_d -= 10
        aces_d -= 1
    dealer_bust = sum_d > 21
    player_natural = len(player) == 2 and 1 in player and (10 in player)
    dealer_natural = len(dealer) == 2 and 1 in dealer and (10 in dealer)
    if player_bust:
        return -1.0
    elif dealer_bust:
        return 1.0
    elif player_natural and dealer_natural:
        return 0.0
    elif player_natural:
        return 1.5
    elif dealer_natural:
        return -1.0
    elif sum_p > sum_d:
        return 1.0
    elif sum_p < sum_d:
        return -1.0
    else:
        return 0.0

