def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        activated = np.where(z >= 0, z, a * z)
        return activated.tolist()
    elif isinstance(z, list):
        return [leaky_relu(e, a) for e in z]
    elif z >= 0:
        return z
    else:
        return a * z

import math
from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    feature = list(feature)
    target = list(target)
    if not feature or not target:
        return (None, 0.0)
    n = len(feature)
    if n != len(target):
        raise ValueError('Feature and target must be of the same length.')
    sorted_feature = sorted(feature)
    unique_sorted = []
    prev = None
    for val in sorted_feature:
        if val != prev:
            unique_sorted.append(val)
            prev = val
    if len(unique_sorted) < 2:
        return (None, 0.0)
    candidate_thresholds = []
    for i in range(len(unique_sorted) - 1):
        t = (unique_sorted[i] + unique_sorted[i + 1]) / 2
        candidate_thresholds.append(t)
    counts_parent = Counter(target)
    H_parent = 0.0
    for (class_label, count) in counts_parent.items():
        p = count / n
        H_parent -= p * math.log2(p)
    if H_parent == 0.0:
        return (None, 0.0)
    max_ig = -1.0
    best_t = None
    for t in candidate_thresholds:
        left_counts = Counter()
        right_counts = Counter()
        for (val, label) in zip(feature, target):
            if val < t:
                left_counts[label] += 1
            else:
                right_counts[label] += 1
        left_size = sum(left_counts.values())
        right_size = n - left_size
        H_L = 0.0
        for count in left_counts.values():
            p = count / left_size
            H_L -= p * math.log2(p)
        H_R = 0.0
        for count in right_counts.values():
            p = count / right_size
            H_R -= p * math.log2(p)
        IG = H_parent - left_size / n * H_L - right_size / n * H_R
        if IG > max_ig:
            max_ig = IG
            best_t = t
        elif IG == max_ig:
            if best_t is None or t < best_t:
                best_t = t
    if max_ig <= 0.0:
        return (None, 0.0)
    else:
        best_t_rounded = round(best_t, 4)
        max_ig_rounded = round(max_ig, 4)
        return (best_t_rounded, max_ig_rounded)

