def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    support_counts = {}
    for trans in transactions:
        unique_items = set(trans)
        for item in unique_items:
            support_counts[item] = support_counts.get(item, 0) + 1
    frequent_items = {item: count for (item, count) in support_counts.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    items = list(frequent_items.keys())
    items.sort(key=lambda x: (-frequent_items[x], x))
    root = {'support': 0, 'children': {}}
    for trans in transactions:
        unique_items = set(trans)
        filtered = [item for item in unique_items if item in frequent_items]
        filtered_sorted = sorted(filtered, key=lambda x: (-frequent_items[x], x))
        current_node = root
        for item in filtered_sorted:
            if item in current_node['children']:
                current_node = current_node['children'][item]
                current_node['support'] += 1
            else:
                new_node = {'support': 1, 'children': {}}
                current_node['children'][item] = new_node
                current_node = new_node
    return root

