def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    x_arr = np.asarray(x)
    is_scalar = x_arr.ndim == 0
    sigma = 1 / (1 + np.exp(-x_arr))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    rounded = np.round(result, 4)
    if is_scalar:
        return rounded.item()
    else:
        return rounded.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    unique_items = sorted({item for transaction in transactions for item in transaction})
    total_transactions = len(transactions)
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    current_level = []
    freq_itemsets = []
    for item in unique_items:
        support = item_counts.get(item, 0) / total_transactions
        if support >= min_sup:
            current_level.append((item,))
            freq_itemsets.append((item,))
    k = 2
    while current_level:
        groups = {}
        for itemset in current_level:
            prefix = tuple(itemset[:-1]) if len(itemset) > 1 else ()
            if prefix not in groups:
                groups[prefix] = []
            groups[prefix].append(itemset)
        next_level = []
        seen = set()
        for (prefix, group) in groups.items():
            group_sorted = sorted(group, key=lambda x: x[-1])
            for i in range(len(group_sorted)):
                for j in range(i + 1, len(group_sorted)):
                    a = group_sorted[i]
                    b = group_sorted[j]
                    if b[-1] > a[-1]:
                        new_candidate = tuple(sorted(prefix + (a[-1], b[-1])))
                        if new_candidate not in seen:
                            seen.add(new_candidate)
                            next_level.append(new_candidate)
        candidate_support = {}
        for candidate in next_level:
            count = 0
            for transaction in transactions:
                if all((item in transaction for item in candidate)):
                    count += 1
            support = count / total_transactions
            if support >= min_sup:
                candidate_support[candidate] = True
            else:
                candidate_support[candidate] = False
        current_level = [candidate for candidate in next_level if candidate_support.get(candidate, False)]
        freq_itemsets.extend(current_level)
        if not current_level:
            break
        k += 1
    freq_itemsets.sort(key=lambda x: (len(x), x))
    return freq_itemsets

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not isinstance(X, list) or not all((isinstance(row, list) for row in X)):
        return -1
    if not X:
        return -1
    C = len(X[0])
    for row in X:
        if len(row) != C:
            return -1
    if Y is not None:
        if not isinstance(Y, list) or not all((isinstance(row, list) for row in Y)):
            return -1
        if not Y:
            return -1
        if len(Y[0]) != C:
            return -1
    if sigma is None:
        sigma_val = np.sqrt(C / 2)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_val = [sigma] * C
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        for s in sigma:
            if not isinstance(s, (int, float)) or s <= 0:
                return -1
        sigma_val = sigma
    else:
        return -1
    X_np = np.array(X)
    if Y is None:
        Y_np = X_np
    else:
        Y_np = np.array(Y)
    diff = X_np[:, np.newaxis] - Y_np
    sigma_np = np.array(sigma_val).reshape(1, 1, -1)
    diff_scaled = diff / sigma_np
    squared_diff = diff_scaled ** 2
    sum_squared = np.sum(squared_diff, axis=2)
    kernel = np.exp(-0.5 * sum_squared)
    kernel_rounded = np.round(kernel, 4)
    result = kernel_rounded.tolist()
    return result

def expected_sarsa_update(q_table: list[list[float]], state: int, action: int, reward: float, next_state: int | None, epsilon: float, lr: float, gamma: float) -> list[list[float]]:
    """One step Expected–SARSA TD(0) update for a tabular Q function."""
    q_table_new = [row.copy() for row in q_table]
    if next_state is None:
        target = reward
    else:
        s = next_state
        n_actions = len(q_table_new[s])
        max_q = max(q_table_new[s])
        greedy_action = None
        for a in range(n_actions):
            if q_table_new[s][a] == max_q:
                greedy_action = a
                break
        expected_q = 0.0
        for a in range(n_actions):
            if a == greedy_action:
                prob = 1 - epsilon + epsilon / n_actions
            else:
                prob = epsilon / n_actions
            expected_q += prob * q_table_new[s][a]
        target = reward + gamma * expected_q
    q_table_new[state][action] += lr * (target - q_table_new[state][action])
    rounded_q_table = []
    for row in q_table_new:
        rounded_row = [round(x, 4) for x in row]
        rounded_q_table.append(rounded_row)
    return rounded_q_table

