            if child.count >= min_sup or child.children:
                mine_tree(child, new_path)
    mine_tree(root, [])
    sorted_result = []
    for item_set in result:
        sorted_set = tuple(sorted(item_set))
        sorted_result.append(sorted_set)
    sorted_result.sort(key=lambda x: (len(x), x))
    return sorted_result

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    if task_type == 'classification':
        feature_index = root.label
        feature_value = features[feature_index]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    else:
        feature_index = root.label
        feature_value = features[feature_index]
        if feature_value <= root.s:
            child = root.child[0]
        else:
            child = root.child[1]
        return predict_sample(child, features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Implement Maximum Entropy classifier with Generalized Iterative Scaling."""
    possible_labels = set(y_train)
    N = len(X_train)
    if N == 0:
        return []
    counts = defaultdict(int)
    for k in range(N):
        x = X_train[k]
        y = y_train[k]
        for i in range(x.shape[1]):
            v = x[i]
            j = (i, v, y)
            counts[j] += 1
    E = {j: counts[j] / N for j in counts}
    weights = defaultdict(float)
    for _ in range(n_iter):
        M = defaultdict(float)
        for k in range(N):
            x = X_train[k]
            sum_wy = defaultdict(float)
            for i in range(x.shape[1]):
                v = x[i]
                for y in possible_labels:
                    j = (i, v, y)
                    sum_wy[y] += weights.get(j, 0.0)
            Z = sum(sum_wy.values())
            for i in range(x.shape[1]):
                v = x[i]
                for y in possible_labels:
                    j = (i, v, y)
                    if Z == 0:
                        p = 0.0
                    else:
                        p = sum_wy[y] / Z
                    M[j] += p
        for j in M:
            M[j] /= N
        changes = []
        for j in E:
            if E[j] == 0 or M[j] == 0:
                continue
            s_j = E[j] / M[j]
            delta = math.log(s_j)
            changes.append(abs(delta))
            weights[j] += delta
        if changes:
            max_change = max(changes)
        else:
            max_change = 0.0
        if max_change < epsilon:
            break
    predicted_labels = []
    for x in X_test:
        sum_wy = defaultdict(float)
        for i in range(x.shape[1]):
            v = x[i]
            for y in possible_labels:
                j = (i, v, y)
                sum_wy[y] += weights.get(j, 0.0)
        Z = sum(sum_wy.values())
        if Z == 0:
            predicted_labels.append(next(iter(possible_labels)))
            continue
        best_y = None
        max_p = -1
        for y in possible_labels:
            p = sum_wy[y] / Z
            if p > max_p:
                max_p = p
                best_y = y
        predicted_labels.append(best_y)
    return predicted_labels
