def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if not a:
        return [] if not b else -1
    num_cols = len(a[0])
    for row in a:
        if len(row) != num_cols:
            return -1
    if len(b) != num_cols:
        return -1
    result = []
    for row in a:
        dot = 0
        for i in range(num_cols):
            dot += row[i] * b[i]
        result.append(dot)
    return result
