def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    if mode == 'row':
        means = []
        for row in matrix:
            mean = sum(row) / len(row)
            means.append(mean)
        return means
    else:
        if not matrix:
            return []
        n_cols = len(matrix[0])
        means = []
        for i in range(n_cols):
            column = [row[i] for row in matrix]
            mean = sum(column) / len(column)
            means.append(mean)
        return means
