assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'row') == [2.0, 5.0, 8.0]

def scalar_multiply(matrix: list[list[int | float]], scalar: int | float) -> list[list[int | float]]:
    result = []
    for row in matrix:
        new_row = []
        for num in row:
            new_row.append(num * scalar)
        result.append(new_row)
    return result
