def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]]:
    if not a or not b:
        return -1
    rows_a = len(a)
    cols_a = len(a[0])
    for row in a:
        if len(row) != cols_a:
            return -1
    rows_b = len(b)
    cols_b = len(b[0])
    for row in b:
        if len(row) != cols_b:
            return -1
    if cols_a != rows_b:
        return -1
    result = []
    for i in range(rows_a):
        new_row = []
        for j in range(cols_b):
            sum_val = 0
            for k in range(cols_a):
                sum_val += a[i][k] * b[k][j]
            new_row.append(sum_val)
        result.append(new_row)
    return result
