class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        super().__init__()
        self.n_units = n_units
        self.input_shape = input_shape
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = None
        self.optimizer_w0 = None
        self.layer_input = None

    def initialize(self):
        if self.input_shape is None:
            raise ValueError('Input shape must be set before initializing the layer.')
        limit = 1.0 / math.sqrt(self.input_shape[0])
        self.W = np.random.uniform(low=-limit, high=limit, size=(self.input_shape[0], self.n_units))
        self.w0 = np.zeros(self.n_units)

    def parameters(self):
        if self.input_shape is None:
            return 0
        return self.input_shape[0] * self.n_units + self.n_units

    def forward_pass(self, X, training):
        self.layer_input = X.copy()
        output = np.dot(X, self.W) + self.w0
        return output

    def backward_pass(self, accum_grad):
        dL_dW = np.dot(self.layer_input.T, accum_grad)
        dL_dw0 = np.sum(accum_grad, axis=0)
        dL_dX = np.dot(accum_grad, self.W.T)
        if self.trainable:
            if self.optimizer_W is not None and self.optimizer_w0 is not None:
                self.W = self.optimizer_W.update(self.W, dL_dW)
                self.w0 = self.optimizer_w0.update(self.w0, dL_dw0)
        return dL_dX

    def output_shape(self):
        return (self.n_units,)
