def calculate_brightness(img):
    if not img:
        return -1
    for row in img:
        if not row:
            return -1
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
    total = 0
    count = 0
    for row in img:
        total += sum(row)
        count += len(row)
    average = total / count
    return round(average, 2)
