def rmse(y_true, y_pred):
    if not (isinstance(y_true, (list, np.ndarray)) and isinstance(y_pred, (list, np.ndarray))):
        raise ValueError('Invalid input type. y_true and y_pred must be lists or numpy arrays.')
    y_true = np.array(y_true)
    y_pred = np.array(y_pred)
    if not (np.issubdtype(y_true.dtype, np.number) and np.issubdtype(y_pred.dtype, np.number)):
        raise ValueError('y_true and y_pred must contain numeric values.')
    if y_true.shape != y_pred.shape:
        raise ValueError('y_true and y_pred must have the same shape.')
    if y_true.size == 0:
        raise ValueError('y_true and y_pred cannot be empty.')
    squared_diff = (y_true - y_pred) ** 2
    mean_squared = np.mean(squared_diff)
    rmse_value = np.sqrt(mean_squared)
    return round(rmse_value, 3)
