def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if not a:
        return [] if not b else -1
    num_cols = len(a[0])
    for row in a:
        if len(row) != num_cols:
            return -1
    if len(b) != num_cols:
        return -1
    result = []
    for row in a:
        dot = 0
        for i in range(num_cols):
            dot += row[i] * b[i]
        result.append(dot)
    return result
assert matrix_dot_vector([[1, 2, 3], [2, 4, 5], [6, 8, 9]], [1, 2, 3]) == [14, 25, 49]
assert matrix_dot_vector([[1, 2], [2, 4], [6, 8], [12, 4]], [1, 2, 3]) == -1
assert matrix_dot_vector([[1.5, 2.5], [3.0, 4.0]], [2, 1]) == [5.5, 10.0]

def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    if mode == 'row':
        means = []
        for row in matrix:
            mean = sum(row) / len(row)
            means.append(mean)
        return means
    else:
        if not matrix:
            return []
        n_cols = len(matrix[0])
        means = []
        for i in range(n_cols):
            column = [row[i] for row in matrix]
            mean = sum(column) / len(column)
            means.append(mean)
        return means
assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'column') == [4.0, 5.0, 6.0]
assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'row') == [2.0, 5.0, 8.0]

def scalar_multiply(matrix: list[list[int | float]], scalar: int | float) -> list[list[int | float]]:
    result = []
    for row in matrix:
        new_row = []
        for num in row:
            new_row.append(num * scalar)
        result.append(new_row)
    return result
assert scalar_multiply([[1,2],[3,4]], 2) == [[2, 4], [6, 8]]
assert scalar_multiply([[0,-1],[1,0]], -1) == [[0, 1], [-1, 0]]

def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]]:
    if not a or not b:
        return -1
    rows_a = len(a)
    cols_a = len(a[0])
    for row in a:
        if len(row) != cols_a:
            return -1
    rows_b = len(b)
    cols_b = len(b[0])
    for row in b:
        if len(row) != cols_b:
            return -1
    if cols_a != rows_b:
        return -1
    result = []
    for i in range(rows_a):
        new_row = []
        for j in range(cols_b):
            sum_val = 0
            for k in range(cols_a):
                sum_val += a[i][k] * b[k][j]
            new_row.append(sum_val)
        result.append(new_row)
    return result
assert matrixmul([[1,2,3],[2,3,4],[5,6,7]],[[3,2,1],[4,3,2],[5,4,3]]) == [[26, 20, 14], [38, 29, 20], [74, 56, 38]]
assert matrixmul([[0,0],[2,4],[1,2]],[[0,0],[2,4]]) == [[0, 0], [8, 16], [4, 8]]
assert matrixmul([[0,0],[2,4],[1,2]],[[0,0,1],[2,4,1],[1,2,3]]) == -1

