def OSA(source: str, target: str) -> int:
    m = len(source)
    n = len(target)
    dp = [[0] * (n + 1) for _ in range(m + 1)]
    for i in range(m + 1):
        dp[i][0] = i
    for j in range(n + 1):
        dp[0][j] = j
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            substitution = dp[i - 1][j - 1] + (0 if source[i - 1] == target[j - 1] else 1)
            insertion = dp[i][j - 1] + 1
            deletion = dp[i - 1][j] + 1
            transposition = float('inf')
            if i >= 2 and j >= 2:
                if source[i - 2] == target[j - 1] and source[i - 1] == target[j - 2]:
                    transposition = dp[i - 2][j - 2] + 1
            dp[i][j] = min(substitution, insertion, deletion, transposition)
    return dp[m][n]

