def gauss_seidel(A, b, n, x_ini=None):
    if x_ini is None:
        x = np.zeros_like(b, dtype=np.float64)
    else:
        x = np.array(x_ini, dtype=np.float64)
    for _ in range(n):
        for i in range(len(x)):
            sum1 = 0.0
            for j in range(i):
                sum1 += A[i, j] * x[j]
            sum2 = 0.0
            for j in range(i + 1, len(x)):
                sum2 += A[i, j] * x[j]
            x[i] = (b[i] - sum1 - sum2) / A[i, i]
    x = np.round(x, 4)
    return x.tolist()

