def gaussian_elimination(A, b):
    A = A.copy()
    b = b.copy().flatten()
    n = A.shape[0]
    M = np.hstack((A, b.reshape(-1, 1)))
    for pivot in range(n):
        max_row = pivot
        for i in range(pivot + 1, n):
            if abs(M[i, pivot]) > abs(M[max_row, pivot]):
                max_row = i
        M[[pivot, max_row]] = M[[max_row, pivot]]
        for row in range(pivot + 1, n):
            factor = M[row, pivot] / M[pivot, pivot]
            M[row, pivot:] -= factor * M[pivot, pivot:]
    x = np.zeros(n)
    for i in range(n - 1, -1, -1):
        sum_val = 0.0
        for j in range(i + 1, n):
            sum_val += M[i, j] * x[j]
        x[i] = (M[i, n] - sum_val) / M[i, i]
    x = np.round(x, 4)
    return x.tolist()

def compressed_col_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix into its Compressed Column Sparse (CSC) representation.

    :param dense_matrix: List of lists representing the dense matrix
    :return: Tuple of (values, row indices, column pointer)
    """
    rows = len(dense_matrix)
    if rows == 0:
        return ([], [], [0])
    cols = len(dense_matrix[0])
    values = []
    row_indices = []
    column_pointer = [0]
    for j in range(cols):
        for i in range(rows):
            val = dense_matrix[i][j]
            if val != 0:
                values.append(val)
                row_indices.append(i)
        column_pointer.append(len(values))
    return (values, row_indices, column_pointer)

