def create_row_hv(row, dim, random_seeds):

    def generate_hv(seed, dim):
        rng = np.random.RandomState(seed)
        return rng.choice([-1, 1], size=dim)
    composite_hv = np.zeros(dim)
    for (key, value) in row.items():
        name_hv = generate_hv(0, dim)
        value_seed = random_seeds[key]
        value_hv = generate_hv(value_seed, dim)
        bound_hv = name_hv * value_hv
        composite_hv += bound_hv
    return composite_hv.tolist()

