def layer_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, epsilon: float=1e-05):
    mean = X.mean(axis=2, keepdims=True)
    var = ((X - mean) ** 2).mean(axis=2, keepdims=True)
    denominator = np.sqrt(var + epsilon)
    normalized = (X - mean) / denominator
    output = gamma * normalized + beta
    return output.round(5).tolist()

