def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or len(p) == 0:
        return 0.0
    product = np.multiply(p, q)
    sqrt_product = np.sqrt(product)
    sum_sqrt = np.sum(sqrt_product)
    ln_sum = np.log(sum_sqrt)
    distance = -ln_sum
    return round(distance, 4)

