def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    if len(sequence) < N:
        return 0.0
    if N == 1:
        ngram_counts = Counter()
        for word in corpus:
            ngram = (word,)
            ngram_counts[ngram] += 1
        prev_ngram_count = len(corpus)
    else:
        ngram_counts = Counter()
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            ngram_counts[ngram] += 1
        prev_ngram_counts = Counter()
        for i in range(len(corpus) - (N - 1) + 1):
            prev_ngram = tuple(corpus[i:i + (N - 1)])
            prev_ngram_counts[prev_ngram] += 1
    total_log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        current_ngram = tuple(sequence[i:i + N])
        if current_ngram not in ngram_counts:
            return float('-inf')
        if N > 1:
            prefix = current_ngram[:-1]
            if prefix not in prev_ngram_counts:
                return float('-inf')
        if N == 1:
            numerator = ngram_counts[current_ngram]
            denominator = prev_ngram_count
        else:
            numerator = ngram_counts[current_ngram]
            denominator = prev_ngram_counts[prefix]
        if denominator == 0 or numerator == 0:
            return float('-inf')
        prob = numerator / denominator
        log_prob = np.log(prob)
        total_log_prob += log_prob
    return round(total_log_prob, 4)
