def metric(name: str):
    """Factory producing a validated distance function."""
    if name not in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:

        def d(x, y):
            return -1
        return d

    def d(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        try:
            x = np.array(x, dtype=np.float64)
            y = np.array(y, dtype=np.float64)
        except ValueError:
            return -1
        if x.ndim != 1 or y.ndim != 1:
            return -1
        if len(x) != len(y) or len(x) == 0:
            return -1
        if name == 'cosine':
            norm_x = np.linalg.norm(x)
            norm_y = np.linalg.norm(y)
            if norm_x == 0 or norm_y == 0:
                return -1
        if name == 'euclidean':
            diff = x - y
            squared = diff ** 2
            sum_sq = np.sum(squared)
            distance = np.sqrt(sum_sq)
        elif name == 'manhattan':
            diff = x - y
            abs_diff = np.abs(diff)
            distance = np.sum(abs_diff)
        elif name == 'chebyshev':
            diff = x - y
            abs_diff = np.abs(diff)
            distance = np.max(abs_diff)
        elif name == 'cosine':
            dot = np.dot(x, y)
            distance = 1 - dot / (norm_x * norm_y)
        else:
            return -1
        return round(distance, 4)
    return d
