import numpy as np
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    """Train AdaBoost with decision stumps and predict labels for X_test.

    Args:
        X_train: 2-D NumPy array of shape (m, n) containing the training features.
        y_train: 1-D NumPy array of length m with labels **-1** or **1**.
        X_test: 2-D NumPy array of shape (k, n) containing test features.
        n_clf:   Number of weak classifiers (decision stumps) to build. Must be > 0.

    Returns:
        A Python list of length k, each element being either -1 or 1, the
        predicted class for the corresponding row in `X_test`.
    """
    m = X_train.shape[0]
    n = X_train.shape[1]
    n_clf = max(n_clf, 1)
    w = np.ones(m) / m
    stumps = []
    for _ in range(n_clf):
        best_error = float('inf')
        best_feature = None
        best_threshold = None
        best_polarity = None
        for j in range(n):
            feature_values = np.sort(np.unique(X_train[:, j]))
            if len(feature_values) == 0:
                continue
            elif len(feature_values) == 1:
                thresholds = [feature_values[0]]
            else:
                thresholds = []
                for i in range(len(feature_values) - 1):
                    thresholds.append((feature_values[i] + feature_values[i + 1]) / 2)
            for threshold in thresholds:
                for polarity in [1, -1]:
                    if polarity == 1:
                        predictions = np.where(X_train[:, j] <= threshold, 1, -1)
                    else:
                        predictions = np.where(X_train[:, j] > threshold, 1, -1)
                    error = np.sum(w * (y_train != predictions))
                    if error < best_error:
                        best_error = error
                        best_feature = j
                        best_threshold = threshold
                        best_polarity = polarity
        if best_error == 0:
            alpha = float('inf')
        else:
            alpha = 0.5 * np.log((1 - best_error) / best_error)
        if best_polarity == 1:
            h = np.where(X_train[:, best_feature] <= best_threshold, 1, -1)
        else:
            h = np.where(X_train[:, best_feature] > best_threshold, 1, -1)
        exponent = -alpha * y_train * h
        w = w * np.exp(exponent)
        Z = np.sum(w)
        if Z == 0:
            Z = 1e-10
        w = w / Z
        stumps.append((best_feature, best_threshold, best_polarity, alpha))
        if best_error == 0:
            break
    predictions = []
    for x in X_test:
        total = 0.0
        for (feature, threshold, polarity, alpha) in stumps:
            if polarity == 1:
                if x[feature] <= threshold:
                    h = 1
                else:
                    h = -1
            elif x[feature] > threshold:
                h = 1
            else:
                h = -1
            total += alpha * h
        if total > 0:
            predictions.append(1)
        else:
            predictions.append(-1)
    return predictions
