from collections import Counter
from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries."""
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        for item in unique_items:
            item_counts[item] += 1
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items.keys(), key=lambda x: (-frequent_items[x], x))
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        unique_transaction = list(set(transaction))
        frequent_transaction = [item for item in unique_transaction if item in frequent_items]
        frequent_transaction.sort(key=lambda x: item_order[x])
        current_node = root
        for item in frequent_transaction:
            if item in current_node['children']:
                current_node = current_node['children'][item]
                current_node['support'] += 1
            else:
                new_node = {'support': 1, 'children': {}}
                current_node['children'][item] = new_node
                current_node = new_node
    return root
