def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not X:
        if Y is not None:
            return -1
        else:
            return []
    C = len(X[0])
    for vec in X:
        if len(vec) != C:
            return -1
    if Y is not None:
        if not Y:
            return -1
        c_y = len(Y[0])
        if c_y != C:
            return -1
        for vec in Y:
            if len(vec) != c_y:
                return -1
    if sigma is None:
        sigma_list = [np.sqrt(C / 2)] * C
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_list = [float(sigma)] * C
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        for s in sigma:
            if s <= 0:
                return -1
        sigma_list = sigma
    else:
        return -1
    if Y is None:
        Y_or_X = X
    else:
        Y_or_X = Y
    kernel = []
    for x in X:
        row = []
        for y in Y_or_X:
            sum_sq = 0.0
            for j in range(C):
                diff = x[j] - y[j]
                scaled_diff = diff / sigma_list[j]
                sum_sq += scaled_diff ** 2
            exponent = -0.5 * sum_sq
            kernel_val = np.exp(exponent)
            rounded_val = round(kernel_val, 4)
            row.append(rounded_val)
        kernel.append(row)
    return kernel
