import numpy as np
def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit."""
    if not payoff_probs:
        return (-1.0, -1)
    for p in payoff_probs:
        if p < 0 or p > 1:
            return (-1.0, -1)
    max_p = max(payoff_probs)
    index = payoff_probs.index(max_p)
    return (max_p, index)
assert best_arm([0.15, 0.6, 0.35]) == (0.6, 1), "failed on [0.15, 0.6, 0.35]"
assert best_arm([0.8, 0.8, 0.5]) == (0.8, 0), "failed on tie with first max"
assert best_arm([0.0, 0.0, 0.0]) == (0.0, 0), "failed on all zeros"
assert best_arm([1.0]) == (1.0, 0), "failed on single arm"
assert best_arm([]) == (-1.0, -1), "failed on empty list"
assert best_arm([0.2, -0.1, 0.3]) == (-1.0, -1), "failed on probability < 0"
assert best_arm([0.2, 1.1, 0.3]) == (-1.0, -1), "failed on probability > 1"
assert best_arm([0.333, 0.333, 0.3329]) == (0.333, 0), "failed on close tie"
assert best_arm([0.25, 0.5, 0.75, 0.74]) == (0.75, 2), "failed on normal case"
assert best_arm([0.9, 0.1]) == (0.9, 0), "failed on two arms"
