import numpy as np

class Node:
    def __init__(self, split_point=None, left=None, right=None, value=None):
        self.split_point = split_point
        self.left = left
        self.right = right
        self.value = value

def build_tree(X, y, depth=0, max_depth=3):
    if len(X) < 2 or depth == max_depth:
        return Node(value=np.mean(y))

    best_split_point = None
    best_sse = float('inf')
    for split_point in X:
        left_indices = np.where(X < split_point)[0]
        right_indices = np.where(X >= split_point)[0]
        if len(left_indices) == 0 or len(right_indices) == 0:
            continue
        left_y = y[left_indices]
        right_y = y[right_indices]
        sse = np.sum((left_y - np.mean(left_y))**2) + np.sum((right_y - np.mean(right_y))**2)
        if sse < best_sse:
            best_sse = sse
            best_split_point = split_point

    if best_split_point is None:
        return Node(value=np.mean(y))

    left_indices = np.where(X < best_split_point)[0]
    right_indices = np.where(X >= best_split_point)[0]
    left = build_tree(X[left_indices], y[left_indices], depth=depth+1, max_depth=max_depth)
    right = build_tree(X[right_indices], y[right_indices], depth=depth+1, max_depth=max_depth)
    return Node(best_split_point, left, right)

def predict(tree, X):
    if tree.value is not None:
        return tree.value
    if X < tree.split_point:
        return predict(tree.left, X)
    else:
        return predict(tree.right, X)

def regression_tree_predict(X_train, y_train, X_test, max_depth=3):
    if len(X_train) == 0:
        return []
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    tree = build_tree(X_train, y_train, max_depth=max_depth)
    return [predict(tree, x) for x in X_test]
```

