from collections import Counter, defaultdict
import itertools

class Node:
    def __init__(self, item, count, parent):
        self.item = item
        self.count = count
        self.parent = parent
        self.children = {}
        self.next = None

    def increment(self, count):
        self.count += count

def construct_tree(transactions, min_sup):
    header_table = defaultdict(int)
    for transaction in transactions:
        for item in transaction:
            header_table[item] += 1

    header_table = {k: v for k, v in header_table.items() if v >= min_sup}
    sorted_items = [v[0] for v in sorted(header_table.items(), key=lambda kv: (-kv[1], kv[0]))]
    root = Node(None, None, None)

    for transaction in transactions:
        sorted_transaction = [item for item in transaction if item in header_table]
        sorted_transaction.sort(key=lambda item: sorted_items.index(item))
        current_node = root
        for item in sorted_transaction:
            current_node = current_node.children.setdefault(item, Node(item, 1, current_node))
            header_table[item] += 1

    return root, header_table

def mine_frequent_itemsets(header_table, prefix, frequent_itemsets):
    sorted_items = [v[0] for v in sorted(list(header_table.items()), key=lambda x: header_table[x[0]])]
    for item in sorted_items:
        new_prefix = prefix.copy()
        new_prefix.add(item)
        frequent_itemsets.append(tuple(sorted(new_prefix)))
        conditional_pattern_base = {}
        node = header_table[item][0]
        while node is not None:
            parent = node.parent
            for child in parent.children.values():
                if child is not node:
                    count = child.count
                    if count in conditional_pattern_base:
                        conditional_pattern_base[count].append(child.item)
                    else:
                        conditional_pattern_base[count] = [child.item]
            node = parent
        conditional_tree, new_header_table = construct_tree(conditional_pattern_base, 0)
        if conditional_tree.children:
            mine_frequent_itemsets(new_header_table, new_prefix, frequent_itemsets)

def fp_growth(transactions, min_sup):
    root, header_table = construct_tree(transactions, min_sup)
    frequent_itemsets = []
    mine_frequent_itemsets(header_table, set(), frequent_itemsets)
    return frequent_itemsets
```

