class GBDT:

    def __init__(self, n_estimators=10, learning_rate=0.1, max_depth=3):
        self.n_estimators = n_estimators
        self.learning_rate = learning_rate
        self.max_depth = max_depth
        self.trees = []

    def fit(self, X, y):
        self.trees = []
        y_pred = np.full(np.shape(y), np.mean(y))
        for _ in range(self.n_estimators):
            gradient = y - y_pred
            tree = self._build_tree(X, gradient)
            y_pred += np.multiply(self.learning_rate, self._predict(X, tree))
            self.trees.append(tree)

    def _build_tree(self, X, gradient, depth=0):
        (n_samples, n_features) = X.shape
        (best_idx, best_thr) = (None, None)
        min_error = np.inf
        for f in range(n_features):
            thresholds = np.unique(X[:, f])
            for threshold in thresholds:
                left_idx = X[:, f] < threshold
                right_idx = X[:, f] >= threshold
                left_grad = gradient[left_idx]
                right_grad = gradient[right_idx]
                error = np.sum(left_grad ** 2) + np.sum(right_grad ** 2)
                if error < min_error:
                    min_error = error
                    best_idx = f
                    best_thr = threshold
        if best_idx is None or min_error == 0 or depth == self.max_depth:
            return Node(value=np.mean(gradient))
        left_idx = X[:, best_idx] < best_thr
        right_idx = X[:, best_idx] >= best_thr
        left = self._build_tree(X[left_idx], gradient[left_idx], depth + 1)
        right = self._build_tree(X[right_idx], gradient[right_idx], depth + 1)
        return Node(best_idx, best_thr, left, right)

    def _predict(self, X, tree):
        if tree.value is not None:
            return np.full(X.shape[0], tree.value)
        if X[:, tree.feature] < tree.threshold:
            return self._predict(X, tree.left)
        else:
            return self._predict(X, tree.right)

    def predict(self, X):
        y_pred = np.zeros(X.shape[0])
        for tree in self.trees:
            y_pred += np.multiply(self.learning_rate, self._predict(X, tree))
        return y_pred.tolist()
