def reshape_matrix(a: list[list[int | float]], new_shape: tuple[int, int]) -> list[list[int | float]]:
    original_rows = len(a)
    if original_rows == 0:
        original_cols = 0
    else:
        original_cols = len(a[0]) if a[0] else 0
        for row in a:
            if len(row) != original_cols:
                return []
    try:
