def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]] | int:
    if not a or not b:
        return -1
    try:
        cols_a = len(a[0])
        cols_b = len(b[0])
    except IndexError:
        return -1
    rows_b = len(b)
    if cols_a != rows_b:
        return -1
    rows_a = len(a)
    result = [[0 for _ in range(cols_b)] for _ in range(rows_a)]
    for i in range(rows_a):
        for j in range(cols_b):
            total = 0
            for k in range(cols_a):
                if k >= len(a[i]) or j >= len(b[k]):
                    return -1
                total += a[i][k] * b[k][j]
            result[i][j] = total
    return result
