def determinant_4x4(matrix: list[list[int | float]]) -> float:
    n = len(matrix)
    if n == 1:
        return matrix[0][0]
    elif n == 2:
        return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
    else:
        det = 0.0
        for j in range(n):
            minor = [row[:j] + row[j + 1:] for row in matrix[1:]]
            sign = (-1) ** j
            det += matrix[0][j] * sign * determinant_4x4(minor)
        return det
