from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(targets):
        counts = Counter(targets)
        total = len(targets)
        if total == 0:
            return 0.0
        return -sum((count / total * math.log2(count / total) for count in counts.values()))
    target_values = [ex[target_attr] for ex in examples]
    if len(set(target_values)) == 1:
        return {'class': target_values[0]}
    if not attributes:
        majority = Counter(target_values).most_common(1)[0][0]
        return {'class': majority}
    original_entropy = entropy(target_values)
    best_attr = None
    max_info_gain = -float('inf')
    for attr in attributes:
        groups = {}
        for ex in examples:
            val = ex[attr]
            if val not in groups:
                groups[val] = []
            groups[val].append(ex)
        split_entropy = 0.0
        for group in groups.values():
            group_targets = [ex[target_attr] for ex in group]
            split_entropy += len(group) / len(examples) * entropy(group_targets)
        info_gain = original_entropy - split_entropy
        if info_gain > max_info_gain:
            max_info_gain = info_gain
            best_attr = attr
    decision_node = {'attribute': best_attr, 'branches': {}}
    values = {ex[best_attr] for ex in examples}
    for value in values:
        subset = [ex for ex in examples if ex[best_attr] == value]
        remaining_attrs = [a for a in attributes if a != best_attr]
        subtree = learn_decision_tree(subset, remaining_attrs, target_attr)
        decision_node['branches'][value] = subtree
    return decision_node
