class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        super().__init__()
        self.layer_input = None
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.W_opt = None
        self.w0_opt = None
        if input_shape is not None:
            self.set_input_shape(input_shape)

    def initialize(self):
        if self.input_shape is None:
            raise ValueError('Input shape must be set before initializing weights.')
        input_dim = self.input_shape[0]
        limit = 1.0 / math.sqrt(input_dim)
        self.W = np.random.uniform(-limit, limit, (input_dim, self.n_units))
        self.w0 = np.zeros(self.n_units)
        self.W_opt = copy.deepcopy(self.W_opt) if self.W_opt else None
        self.w0_opt = copy.deepcopy(self.w0_opt) if self.w0_opt else None

    def parameters(self):
        if self.W is None or self.w0 is None:
            return 0
        return self.W.size + self.w0.size

    def forward_pass(self, X, training):
        self.layer_input = X
        return np.dot(X, self.W) + self.w0

    def backward_pass(self, accum_grad):
        grad_input = np.dot(accum_grad, self.W.T)
        if self.trainable:
            grad_W = np.dot(self.layer_input.T, accum_grad)
            grad_w0 = np.sum(accum_grad, axis=0, keepdims=False)
            if self.W_opt is not None:
                self.W = self.W_opt.update(self.W, grad_W)
            if self.w0_opt is not None:
                self.w0 = self.w0_opt.update(self.w0, grad_w0)
        return grad_input

    def output_shape(self):
        return (self.n_units,)
