def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    N = len(corpus)
    idf = {}
    for term in query:
        df = 0
        for doc in corpus:
            if term in doc:
                df += 1
        idf_value = np.log((N + 1) / (df + 1))
        idf[term] = idf_value
    result = []
    for doc in corpus:
        doc_length = len(doc)
        doc_scores = []
        for term in query:
            if doc_length == 0:
                tf = 0.0
            else:
                term_count = doc.count(term)
                tf = term_count / doc_length
            tf_idf = tf * idf[term]
            doc_scores.append(round(tf_idf, 4))
        result.append(doc_scores)
    return np.array(result).tolist()
