def calculate_brightness(img):
    if not img:
        return -1
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
    total_pixels = len(img) * row_length
    if total_pixels == 0:
        return -1
    sum_pixels = sum((pixel for row in img for pixel in row))
    average = sum_pixels / total_pixels
    return round(average, 2)
