import numpy as np

def load_encoder_hparams_and_params(model_size: str = "124M", models_dir: str = "models"):
    class DummyBPE:
        def __init__(self):
            self.encoder_dict = {"hello": 1, "world": 2, "<UNK>": 0}

        def encode(self, text: str):
            tokens = text.strip().split()
            return [self.encoder_dict.get(token, self.encoder_dict["<UNK>"]) for token in tokens]

        def decode(self, token_ids: list):
            reversed_dict = {v: k for k, v in self.encoder_dict.items()}
            return " ".join([reversed_dict.get(tok_id, "<UNK>") for tok_id in token_ids])

    hparams = {
        "n_ctx": 1024,
        "n_head": 12
    }

    params = {
        "wte": np.random.rand(3, 10),
        "wpe": np.random.rand(1024, 10),
        "blocks": [],
        "ln_f": {
            "g": np.ones(10),
            "b": np.zeros(10),
        }
    }

    encoder = DummyBPE()
    return encoder, hparams, params
import numpy as np
def layer_norm(x, g, b, eps=1e-05):
    mean = np.mean(x, axis=-1, keepdims=True)
    variance = np.var(x, axis=-1, keepdims=True)
    x = (x - mean) / np.sqrt(variance + eps)
    return g * x + b
def softmax(x):
    exp_x = np.exp(x - np.max(x, axis=-1, keepdims=True))
    return exp_x / exp_x.sum(axis=-1, keepdims=True)
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    input_ids = encoder.encode(prompt)
    for _ in range(n_tokens_to_generate):
        current_input = input_ids[-hparams['n_ctx']:]
        n_tokens = len(current_input)
        token_emb = params['wte'][current_input]
        positions = np.arange(n_tokens)
        pos_emb = params['wpe'][positions]
        embeddings = token_emb + pos_emb
        attn_output = embeddings.copy()
        ff_output = np.maximum(attn_output, 0)
        norm_output = layer_norm(ff_output, params['ln_f']['g'], params['ln_f']['b'])
        logits = np.dot(norm_output, params['wte'].T)
        next_token_logits = logits[-1]
        probs = softmax(next_token_logits)
        next_token = np.argmax(probs)
        input_ids.append(int(next_token))
    generated_text = encoder.decode(input_ids)
    return generated_text
assert gen_text("hello", 5) == "hello hello hello <UNK> <UNK>"
assert gen_text("hello world", 10) == "world world world world world world world world world world"
assert gen_text("world", 3) == "world world world"

