from collections import Counter, defaultdict
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):
    ref_words = reference.split()
    cand_words = candidate.split()
    if not ref_words or not cand_words:
        return 0.0
    ref_counts = Counter(ref_words)
    cand_counts = Counter(cand_words)
    matches = 0
    for word in cand_counts:
        if word in ref_counts:
            matches += min(ref_counts[word], cand_counts[word])
    if matches == 0:
        return 0.0
    precision = matches / len(cand_words) if len(cand_words) > 0 else 0.0
    recall = matches / len(ref_words) if len(ref_words) > 0 else 0.0
    denominator = alpha * precision + (1 - alpha) * recall
    if denominator == 0:
        f_mean = 0.0
    else:
        f_mean = precision * recall / denominator
    ref_word_positions = defaultdict(list)
    for (idx, word) in enumerate(ref_words):
        ref_word_positions[word].append(idx)
    used_positions = set()
    candidate_positions = []
    for word in cand_words:
        if word in ref_word_positions:
            for pos in ref_word_positions[word]:
                if pos not in used_positions:
                    used_positions.add(pos)
                    candidate_positions.append(pos)
                    break
    chunks = 1
    for i in range(1, len(candidate_positions)):
        if candidate_positions[i] != candidate_positions[i - 1] + 1:
            chunks += 1
    penalty = gamma * (chunks / matches) ** beta if matches > 0 else 0.0
    score = f_mean * (1 - penalty)
    score = max(0.0, score)
    return round(score, 3)
assert meteor_score('The dog barks at the moon', 'The dog barks at the moon') == 0.998
assert meteor_score('Rain falls gently from the sky', 'Gentle rain drops from the sky') == 0.625
assert meteor_score('The sun shines brightly', 'Clouds cover the sky') == 0.125
