import math
from collections import defaultdict
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**."""
    if not transactions or minsup <= 0:
        return {}
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    if min_support_count < 1:
        min_support_count = 1
    transaction_sets = [set(t) for t in transactions]
    item_counter = defaultdict(int)
    for t in transaction_sets:
        for item in t:
            item_counter[item] += 1
    frequent_items = {item for (item, count) in item_counter.items() if count >= min_support_count}
    frequent_itemsets = {}
    previous_level = [frozenset([item]) for item in sorted(frequent_items)]
    for itemset in previous_level:
        itemset_tuple = tuple(sorted(itemset))
        frequent_itemsets[itemset_tuple] = item_counter[next(iter(itemset))]
    k = 2
    while previous_level:
        current_level = []
        candidates = set()
        for i in range(len(previous_level)):
            for j in range(i + 1, len(previous_level)):
                itemset1 = previous_level[i]
                itemset2 = previous_level[j]
                new_candidate = itemset1.union(itemset2)
                if len(new_candidate) == k:
                    candidates.add(new_candidate)
        candidates = list(candidates)
        candidate_support = defaultdict(int)
        for candidate in candidates:
            candidate_tuple = tuple(sorted(candidate))
            for t in transaction_sets:
                if candidate.issubset(t):
                    candidate_support[candidate_tuple] += 1
        new_frequent_itemsets = {}
        for (itemset_tuple, count) in candidate_support.items():
            if count >= min_support_count:
                new_frequent_itemsets[itemset_tuple] = count
        if not new_frequent_itemsets:
            break
        frequent_itemsets.update(new_frequent_itemsets)
        previous_level = [frozenset(itemset) for itemset in new_frequent_itemsets.keys()]
        k += 1
    return frequent_itemsets

import numpy as np
from collections import defaultdict
def knn_recommend(data: np.ndarray, user_ind: int, k: int, criterion: str='cosine') -> list[int]:
    """Item-based k-NN collaborative filtering recommender."""
    (n_users, n_items) = data.shape
    S = np.zeros((n_items, n_items))
    for i in range(n_items):
        for j in range(i + 1, n_items):
            rated_both = (data[:, i] > 0) & (data[:, j] > 0)
            v1 = data[rated_both, i]
            v2 = data[rated_both, j]
            if len(v1) == 0:
                S[i, j] = 0
                S[j, i] = 0
                continue
            if criterion == 'cosine':
                if np.std(v1) > 0.001:
                    v1 = v1 - np.mean(v1)
                if np.std(v2) > 0.001:
                    v2 = v2 - np.mean(v2)
                dot_product = np.dot(v1, v2)
                norm_v1 = np.linalg.norm(v1)
                norm_v2 = np.linalg.norm(v2)
                if norm_v1 > 0 and norm_v2 > 0:
                    similarity = dot_product / (norm_v1 * norm_v2)
                else:
                    similarity = 0
            elif criterion == 'pearson':
                if len(v1) < 2:
                    similarity = 0
                else:
                    corr_matrix = np.corrcoef(v1, v2)
                    similarity = corr_matrix[0, 1] if not np.isnan(corr_matrix[0, 1]) else 0
            else:
                raise ValueError("Invalid criterion. Choose 'cosine' or 'pearson'.")
            S[i, j] = similarity
            S[j, i] = similarity
    active_user_ratings = data[user_ind]
    rated_items = np.where(active_user_ratings > 0)[0]
    unrated_items = np.where(active_user_ratings == 0)[0]
    if len(rated_items) == 0 or len(unrated_items) == 0:
        return []
    r = active_user_ratings[rated_items]
    scores = defaultdict(float)
    for t in unrated_items:
        numerator = 0.0
        denominator = 0.0
        for i in rated_items:
            sim = S[t, i]
            numerator += r[i] * sim
            denominator += abs(sim)
        if denominator != 0:
            score = numerator / denominator
        else:
            score = 0.0
        scores[t] = score
    sorted_items = sorted(scores.keys(), key=lambda x: (-scores[x], x))
    return sorted_items[:k]

