import math
def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    """Trains a 1-D AdaBoost ensemble and returns predictions."""
    x_train = np.array(x_train)
    y_train = np.array(y_train)
    x_test = np.array(x_test)
    n_samples = len(x_train)
    weights = np.ones(n_samples) / n_samples
    stumps = []
    alphas = []
    while True:
        sorted_indices = np.argsort(x_train)
        sorted_x = x_train[sorted_indices]
        sorted_y = y_train[sorted_indices]
        sorted_weights = weights[sorted_indices]
        best_error = float('inf')
        best_stump = None
        best_predictions = None
        for i in range(n_samples + 1):
            if i == 0:
                theta = -np.inf
            elif i == n_samples:
                theta = np.inf
            else:
                theta = (sorted_x[i - 1] + sorted_x[i]) / 2
            for d in [0, 1]:
                if d == 0:
                    predictions = np.where(x_train <= theta, 1, -1)
                else:
                    predictions = np.where(x_train > theta, 1, -1)
                incorrect = predictions != y_train
                error = np.sum(weights[incorrect])
                if error < best_error:
                    best_error = error
                    best_stump = (d, theta)
                    best_predictions = predictions.copy()
        if best_error <= 1e-10:
            alpha = 1.0
        else:
            alpha = 0.5 * math.log((1 - best_error) / best_error)
        stumps.append(best_stump)
        alphas.append(alpha)
        weights *= np.exp(-alpha * y_train * best_predictions)
        weights /= np.sum(weights)
        current_predictions = np.zeros_like(y_train, dtype=float)
        for ((d, theta), alpha_i) in zip(stumps, alphas):
            if d == 0:
                pred = np.where(x_train <= theta, 1, -1)
            else:
                pred = np.where(x_train > theta, 1, -1)
            current_predictions += alpha_i * pred
        final_predictions = np.sign(current_predictions)
        training_error = np.mean(final_predictions != y_train)
        if training_error <= epsilon:
            break
    test_predictions = np.zeros(len(x_test), dtype=float)
    for ((d, theta), alpha_i) in zip(stumps, alphas):
        if d == 0:
            pred = np.where(x_test <= theta, 1, -1)
        else:
            pred = np.where(x_test > theta, 1, -1)
        test_predictions += alpha_i * pred
    final_test_predictions = np.sign(test_predictions)
    final_test_predictions[final_test_predictions == 0] = 1
    return final_test_predictions.astype(int).tolist()

import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid) in a numerically stable way.

    Args:
        x: Input scalar, list, or NumPy array.
        deriv: If True, compute the derivative (sigmoid) instead of SoftPlus.

    Returns:
        Result rounded to 4 decimal places, as a float (if input is scalar) or list.
    """
    if deriv:

        def sigmoid(z):
            mask = z >= 0
            out = np.zeros_like(z)
            exp_z = np.exp(-np.abs(z))
            out[mask] = 1 / (1 + exp_z[mask])
            out[~mask] = exp_z[~mask] / (1 + exp_z[~mask])
            return out
        if isinstance(x, (list, np.ndarray)):
            x_arr = np.asarray(x)
            result = sigmoid(x_arr)
            rounded_result = np.round(result, 4)
            return rounded_result.tolist()
        else:
            z = x
            if z >= 0:
                sig = 1.0 / (1.0 + np.exp(-z))
            else:
                exp_z = np.exp(z)
                sig = exp_z / (1.0 + exp_z)
            return round(float(sig), 4)
    else:

        def softplus_func(z):
            mask = z > 0
            out = np.zeros_like(z)
            out[mask] = z[mask] + np.log1p(np.exp(-z[mask]))
            out[~mask] = np.log1p(np.exp(z[~mask]))
            return out
        if isinstance(x, (list, np.ndarray)):
            x_arr = np.asarray(x)
            result = softplus_func(x_arr)
            rounded_result = np.round(result, 4)
            return rounded_result.tolist()
        else:
            z = x
            if z > 0:
                sp = z + np.log1p(np.exp(-z))
            else:
                sp = np.log1p(np.exp(z))
            return round(float(sp), 4)

import numpy as np
from collections import defaultdict, Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    if N <= 0:
        return float('-inf')
    total_log_prob = 0.0
    if len(sequence) < N:
        return float('-inf')
    ngram_counts = Counter()
    prefix_counts = Counter()
    if N == 1:
        total_corpus_length = len(corpus)
        if total_corpus_length == 0:
            return float('-inf')
        ngram_counts.update(corpus)
        for word in sequence:
            count_w = ngram_counts[word]
            if count_w == 0:
                return float('-inf')
            prob = count_w / total_corpus_length
            total_log_prob += np.log(prob)
    else:
        ngrams = [tuple(corpus[i:i + N]) for i in range(len(corpus) - N + 1)]
        ngram_counts.update(ngrams)
        prefixes = [tuple(corpus[i:i + N - 1]) for i in range(len(corpus) - (N - 1) + 1)]
        prefix_counts.update(prefixes)
        for i in range(len(sequence) - N + 1):
            current_ngram = tuple(sequence[i:i + N])
            current_prefix = current_ngram[:-1]
            count_ngram = ngram_counts[current_ngram]
            count_prefix = prefix_counts[current_prefix]
            if count_ngram == 0 or count_prefix == 0:
                return float('-inf')
            prob = count_ngram / count_prefix
            total_log_prob += np.log(prob)
    return round(total_log_prob, 4)

from collections import defaultdict, Counter
class TreeNode:

    def __init__(self, name, count, parent):
        self.name = name
        self.count = count
        self.parent = parent
        self.children = {}
        self.node_link = None

    def increment(self, count):
        self.count += count
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items.keys(), key=lambda x: (-frequent_items[x], x))
    root = TreeNode(name=None, count=0, parent=None)
    header_table = {}
    for item in sorted_items:
        header_table[item] = None
    for transaction in transactions:
        filtered_items = [item for item in transaction if item in frequent_items]
        filtered_items.sort(key=lambda x: (-frequent_items[x], x))
        if not filtered_items:
            continue
        current_node = root
        for item in filtered_items:
            if item in current_node.children:
                current_node.children[item].increment(1)
            else:
                new_node = TreeNode(item, 1, current_node)
                current_node.children[item] = new_node
                if header_table[item] is None:
                    header_table[item] = new_node
                else:
                    node = header_table[item]
                    while node.node_link is not None:
                        node = node.node_link
                    node.node_link = new_node
            current_node = current_node.children[item]

    def mine_tree(header_table, min_support, prefix, frequent_itemsets):
        items = sorted(header_table.keys(), key=lambda x: (frequent_items[x], x))
        for item in items:
            new_prefix = prefix.copy()
            new_prefix.append(item)
            new_prefix_sorted = sorted(new_prefix)
            frequent_itemsets.append(new_prefix_sorted)
            conditional_patterns = []
            node = header_table[item]
            while node is not None:
                path = []
                parent = node.parent
                while parent.name is not None:
                    path.append(parent.name)
                    parent = parent.parent
                if path:
                    conditional_patterns.append((path, node.count))
                node = node.node_link
            conditional_item_counts = Counter()
            for (path, count) in conditional_patterns:
                for item_in_path in path:
                    conditional_item_counts[item_in_path] += count
            conditional_frequent_items = {item: count for (item, count) in conditional_item_counts.items() if count >= min_support}
            if conditional_frequent_items:
                new_header_table = {}
                for item_name in conditional_frequent_items:
                    new_header_table[item_name] = None
                conditional_root = TreeNode(name=None, count=0, parent=None)
                for (path, count) in conditional_patterns:
                    filtered_path = [item for item in path if item in conditional_frequent_items]
                    filtered_path.sort(key=lambda x: (-conditional_item_counts[x], x))
                    if not filtered_path:
                        continue
                    current_node = conditional_root
                    for item_in_path in filtered_path:
                        if item_in_path in current_node.children:
                            current_node.children[item_in_path].increment(count)
                        else:
                            new_node = TreeNode(item_in_path, count, current_node)
                            current_node.children[item_in_path] = new_node
                            if new_header_table[item_in_path] is None:
                                new_header_table[item_in_path] = new_node
                            else:
                                temp_node = new_header_table[item_in_path]
                                while temp_node.node_link is not None:
                                    temp_node = temp_node.node_link
                                temp_node.node_link = new_node
                        current_node = current_node.children[item_in_path]
                mine_tree(new_header_table, min_support, new_prefix, frequent_itemsets)
    frequent_itemsets = []
    mine_tree(header_table, min_support, [], frequent_itemsets)
    for item in sorted_items:
        frequent_itemsets.append([item])
    unique_itemsets = []
    seen = set()
    for itemset in frequent_itemsets:
        key = tuple(sorted(itemset))
        if key not in seen:
            seen.add(key)
            unique_itemsets.append(sorted(list(key)))
    unique_itemsets.sort(key=lambda x: (len(x), x))
    return unique_itemsets

import numpy as np
from typing import Any
def mse(bandit: Any, policy: Any) -> float:
    """Compute the mean-squared error between a policy's estimates and truth.

    Parameters
    ----------
    bandit : Any
        Object or dictionary that stores the true expected payout of each arm
        under the key/attribute ``arm_evs``.
    policy : Any
        Object or dictionary that stores the policy's current estimate of each
        arm's expectation under the key/attribute ``ev_estimates``. The field
        must be a dictionary mapping an arm index (int) to a float value.

    Returns
    -------
    float
        The mean-squared error rounded to 4 decimal places. If the policy does
        not provide any estimates the function returns ``numpy.nan``.
    """
    try:
        if hasattr(policy, 'ev_estimates'):
            ev_estimates = policy.ev_estimates
        elif isinstance(policy, dict):
            ev_estimates = policy.get('ev_estimates', {})
        else:
            ev_estimates = {}
    except AttributeError:
        ev_estimates = {}
    if not ev_estimates:
        return np.nan
    try:
        if hasattr(bandit, 'arm_evs'):
            arm_evs = bandit.arm_evs
        elif isinstance(bandit, dict):
            arm_evs = bandit.get('arm_evs', [])
        else:
            arm_evs = []
    except AttributeError:
        arm_evs = []
    if not arm_evs:
        return np.nan
    sorted_indices = sorted(ev_estimates.keys())
    squared_errors = []
    for i in sorted_indices:
        if i >= len(arm_evs):
            continue
        true_ev = arm_evs[i]
        estimated_ev = ev_estimates[i]
        squared_error = (estimated_ev - true_ev) ** 2
        squared_errors.append(squared_error)
    if not squared_errors:
        return np.nan
    mse_value = sum(squared_errors) / len(squared_errors)
    return round(mse_value, 4)

import math
from collections import Counter, defaultdict
def additive_ngram_log_prob(corpus: list[str], sequence: str, N: int, K: float=1.0) -> float:
    """Compute the additive-smoothed log-probability of *sequence* given a corpus."""
    if N <= 0:
        return 0.0
    vocabulary = set()
    for sentence in corpus:
        words = sentence.split()
        vocabulary.update(words)
    vocabulary.add('<UNK>')
    V = len(vocabulary)
    sequence_words = sequence.split()
    processed_sequence = []
    for word in sequence_words:
        if word in vocabulary:
            processed_sequence.append(word)
        else:
            processed_sequence.append('<UNK>')
    if len(processed_sequence) < N:
        return 0.0
    ngram_counts = defaultdict(Counter)
    context_counts = defaultdict(Counter)
    for n in range(1, N + 1):
        for sentence in corpus:
            words = sentence.split()
            processed_words = []
            for word in words:
                if word in vocabulary:
                    processed_words.append(word)
                else:
                    processed_words.append('<UNK>')
            for i in range(len(processed_words) - n + 1):
                ngram = tuple(processed_words[i:i + n])
                context = ngram[:-1]
                word = ngram[-1]
                ngram_counts[n][ngram] += 1
                if n > 1:
                    context_counts[n][context] += 1
    total_words = sum(ngram_counts[1].values()) if 1 in ngram_counts else 0
    log_prob = 0.0
    for i in range(len(processed_sequence) - N + 1):
        ngram = tuple(processed_sequence[i:i + N])
        context = ngram[:-1]
        word = ngram[-1]
        if N == 1:
            count_ngram = ngram_counts[N].get(ngram, 0)
            numerator = count_ngram + K
            denominator = total_words + K * V
        else:
            count_ngram = ngram_counts[N].get(ngram, 0)
            count_context = context_counts[N].get(context, 0)
            numerator = count_ngram + K
            denominator = count_context + K * V
        prob = numerator / denominator
        log_prob += math.log(prob)
    return round(log_prob, 4)

from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if grid[0][0] == 'F' or grid[-1][-1] == 'F':
        return -1
    if n == 1 and grid[0][0] == '.':
        return 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[float('inf')] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] == '#' or fire_time[nx][ny] != float('inf'):
                    continue
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    agent_queue = deque()
    visited = [[False] * n for _ in range(n)]
    agent_queue.append((0, 0, 0))
    visited[0][0] = True
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] == '#' or visited[nx][ny]:
                    continue
                if fire_time[nx][ny] > time + 1 or (nx == n - 1 and ny == n - 1 and (fire_time[nx][ny] >= time + 1)):
                    visited[nx][ny] = True
                    agent_queue.append((nx, ny, time + 1))
    return -1

import numpy as np
class TreeNode:

    def __init__(self, feature_idx=None, threshold=None, left=None, right=None, value=None):
        self.feature_idx = feature_idx
        self.threshold = threshold
        self.left = left
        self.right = right
        self.value = value
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""

    def gini(y):
        classes = np.unique(y)
        gini = 1.0
        for cls in classes:
            p = np.sum(y == cls) / len(y)
            gini -= p ** 2
        return gini

    def find_best_split(X, y):
        best_gini_gain = -1
        best_feature = None
        best_threshold = None
        parent_gini = gini(y)
        (n_samples, n_features) = X.shape
        for feature_idx in range(n_features):
            feature_values = X[:, feature_idx]
            unique_values = np.unique(feature_values)
            for i in range(1, len(unique_values)):
                threshold = (unique_values[i - 1] + unique_values[i]) / 2
                left_mask = feature_values <= threshold
                right_mask = feature_values > threshold
                y_left = y[left_mask]
                y_right = y[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                left_gini = gini(y_left)
                right_gini = gini(y_right)
                total_gini = len(y_left) / n_samples * left_gini + len(y_right) / n_samples * right_gini
                gini_gain = parent_gini - total_gini
                if gini_gain > best_gini_gain:
                    best_gini_gain = gini_gain
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold, best_gini_gain)

    def build_tree(X, y, depth=0):
        if len(np.unique(y)) == 1:
            return TreeNode(value=y[0])
        if max_depth is not None and depth >= max_depth:
            majority_class = np.argmax(np.bincount(y))
            return TreeNode(value=majority_class)
        (feature_idx, threshold, gini_gain) = find_best_split(X, y)
        if feature_idx is None or gini_gain <= 0:
            majority_class = np.argmax(np.bincount(y))
            return TreeNode(value=majority_class)
        left_mask = X[:, feature_idx] <= threshold
        right_mask = ~left_mask
        if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
            majority_class = np.argmax(np.bincount(y))
            return TreeNode(value=majority_class)
        left_subtree = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_subtree = build_tree(X[right_mask], y[right_mask], depth + 1)
        return TreeNode(feature_idx=feature_idx, threshold=threshold, left=left_subtree, right=right_subtree)

    def predict_sample(node, x):
        if node.value is not None:
            return node.value
        if x[node.feature_idx] <= node.threshold:
            return predict_sample(node.left, x)
        else:
            return predict_sample(node.right, x)
    tree = build_tree(X_train, y_train)
    predictions = []
    for sample in X_test:
        predictions.append(predict_sample(tree, sample))
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def d(x, y):
        try:
            x_arr = np.array(x, dtype=float)
            y_arr = np.array(y, dtype=float)
            if x_arr.ndim != 1 or y_arr.ndim != 1:
                return -1
            if len(x_arr) == 0 or len(y_arr) == 0:
                return -1
            if len(x_arr) != len(y_arr):
                return -1
            if name == 'cosine':
                norm_x = np.linalg.norm(x_arr)
                norm_y = np.linalg.norm(y_arr)
                if norm_x == 0 or norm_y == 0:
                    return -1
            if name == 'euclidean':
                distance = np.sqrt(np.sum((x_arr - y_arr) ** 2))
            elif name == 'manhattan':
                distance = np.sum(np.abs(x_arr - y_arr))
            elif name == 'chebyshev':
                distance = np.max(np.abs(x_arr - y_arr))
            elif name == 'cosine':
                dot_product = np.dot(x_arr, y_arr)
                norm_product = np.linalg.norm(x_arr) * np.linalg.norm(y_arr)
                distance = 1.0 - dot_product / norm_product
            else:
                return -1
            return round(distance, 4)
        except (TypeError, ValueError):
            return -1
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack."""

    def calculate_hand_value(hand: list[int]) -> int:
        total = sum(hand)
        aces = hand.count(1)
        if aces > 0 and total + 10 <= 21:
            total += 10
        return total
    player_value = calculate_hand_value(player)
    dealer_value = calculate_hand_value(dealer)
    if player_value > 21:
        return -1.0
    if dealer_value > 21:
        return 1.0
    player_natural = len(player) == 2 and player_value == 21 and (1 in player) and (10 in player)
    dealer_natural = len(dealer) == 2 and dealer_value == 21 and (1 in dealer) and (10 in dealer)
    if player_natural:
        if not dealer_natural:
            return 1.5
        else:
            return 0.0
    if player_value > dealer_value:
        return 1.0
    elif player_value < dealer_value:
        return -1.0
    else:
        return 0.0

