import math
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    """Train an AdaBoost ensemble of decision stumps and predict labels for X_test.

    Args:
        X_train: 2-D numpy array of shape (n_samples, n_features) with training data.
        y_train: 1-D numpy array of shape (n_samples,) containing class labels (-1 or 1).
        X_test:  2-D numpy array of unseen samples to classify.
        n_clf:   Number of weak learners (decision stumps) to use in the ensemble.

    Returns:
        A Python list with one element per row in *X_test*. Each element must be
        either -1 or 1 indicating the predicted class label.
    """
    (n_samples, n_features) = X_train.shape
    sample_weights = np.ones(n_samples) / n_samples
    classifiers = []
    for _ in range(n_clf):
        best_error = float('inf')
        best_stump = None
        for feature_index in range(n_features):
            feature_values = X_train[:, feature_index]
            unique_values = np.unique(feature_values)
            thresholds = []
            if len(unique_values) > 1:
                for i in range(len(unique_values) - 1):
                    thresholds.append((unique_values[i] + unique_values[i + 1]) / 2)
            else:
                thresholds = [unique_values[0]]
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(n_samples)
                    if polarity == 1:
                        predictions[X_train[:, feature_index] < threshold] = -1
                    else:
                        predictions[X_train[:, feature_index] < threshold] = 1
                    misclassified = predictions != y_train
                    error = np.sum(sample_weights * misclassified)
                    if error < best_error:
                        best_error = error
                        best_stump = (feature_index, threshold, polarity)
        if best_error <= 0:
            alpha = 1.0
        else:
            alpha = 0.5 * math.log((1.0 - best_error) / (best_error + 1e-10))
        (feature_index, threshold, polarity) = best_stump
        stump_predictions = np.ones(n_samples)
        if polarity == 1:
            stump_predictions[X_train[:, feature_index] < threshold] = -1
        else:
            stump_predictions[X_train[:, feature_index] < threshold] = 1
        sample_weights *= np.exp(-alpha * y_train * stump_predictions)
        sample_weights /= np.sum(sample_weights)
        classifiers.append((best_stump, alpha))
    predictions = []
    for x in X_test:
        total = 0.0
        for ((feature_index, threshold, polarity), alpha) in classifiers:
            pred = 1
            if polarity == 1:
                if x[feature_index] < threshold:
                    pred = -1
            elif x[feature_index] < threshold:
                pred = 1
            else:
                pred = -1
            total += alpha * pred
        predictions.append(1 if total >= 0 else -1)
    return predictions

import math
from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    combined = list(zip(feature, target))
    combined.sort()
    sorted_feature = [x[0] for x in combined]
    sorted_target = [x[1] for x in combined]
    unique_features = []
    for i in range(len(sorted_feature)):
        if i == 0:
            unique_features.append(sorted_feature[i])
        elif sorted_feature[i] != unique_features[-1]:
            unique_features.append(sorted_feature[i])
    if len(unique_features) <= 1:
        return (None, 0.0)
    thresholds = []
    for i in range(1, len(unique_features)):
        threshold = (unique_features[i - 1] + unique_features[i]) / 2
        thresholds.append(threshold)
    parent_counts = Counter(target)
    total = len(target)
    parent_entropy = 0.0
    for count in parent_counts.values():
        p = count / total
        parent_entropy -= p * math.log2(p) if p > 0 else 0
    max_gain = -float('inf')
    best_threshold = None
    for threshold in thresholds:
        left_targets = []
        right_targets = []
        for (x, y) in zip(sorted_feature, sorted_target):
            if x < threshold:
                left_targets.append(y)
            else:
                right_targets.append(y)
        left_counts = Counter(left_targets)
        right_counts = Counter(right_targets)
        left_entropy = 0.0
        left_total = len(left_targets)
        for count in left_counts.values():
            p = count / left_total if left_total > 0 else 0
            left_entropy -= p * math.log2(p) if p > 0 else 0
        right_entropy = 0.0
        right_total = len(right_targets)
        for count in right_counts.values():
            p = count / right_total if right_total > 0 else 0
            right_entropy -= p * math.log2(p) if p > 0 else 0
        gain = parent_entropy - left_total / total * left_entropy - right_total / total * right_entropy
        gain_rounded = round(gain, 4)
        if gain_rounded > max_gain:
            max_gain = gain_rounded
            best_threshold = threshold
        elif gain_rounded == max_gain:
            if threshold < best_threshold:
                best_threshold = threshold
    if max_gain <= 0.0:
        return (None, 0.0)
    else:
        return (round(best_threshold, 4), max_gain)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        try:
            return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
        except (TypeError, ValueError):
            return tree_a.value == tree_b.value
    elif isinstance(tree_a, Leaf) or isinstance(tree_b, Leaf):
        return False
    else:
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

