from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model."""
    padded_corpus_tokens = []
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        padded_corpus_tokens.append(padded_tokens)
    ngram_counts = Counter()
    prefix_counts = Counter()
    for tokens in padded_corpus_tokens:
        for i in range(len(tokens) - N + 1):
            ngram = tuple(tokens[i:i + N])
            ngram_counts[ngram] += 1
            if N > 1:
                prefix = tuple(tokens[i:i + N - 1])
                prefix_counts[prefix] += 1
    total_tokens = sum((len(tokens) for tokens in padded_corpus_tokens))
    query_tokens = query.split()
    if N > 1:
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    else:
        padded_query = query_tokens
    if len(padded_query) < N:
        return float('-inf')
    total_log_prob = 0.0
    for i in range(len(padded_query) - N + 1):
        ngram = tuple(padded_query[i:i + N])
        if N == 1:
            count = ngram_counts[ngram]
            prob = count / total_tokens
        else:
            count = ngram_counts.get(ngram, 0)
            prefix = tuple(padded_query[i:i + N - 1])
            prefix_count = prefix_counts.get(prefix, 0)
            if prefix_count == 0:
                return float('-inf')
            prob = count / prefix_count
        if prob == 0:
            return float('-inf')
        total_log_prob += math.log(prob)
    return round(total_log_prob, 4)

import numpy as np
def conv2D(X: 'np.ndarray', W: 'np.ndarray', stride: int, pad, dilation: int=0):
    (n_ex, in_rows, in_cols, in_ch) = X.shape
    (kernel_rows, kernel_cols, _, out_ch) = W.shape
    if dilation < 0:
        dilation = 0
    effective_kernel_rows = kernel_rows + (kernel_rows - 1) * dilation
    effective_kernel_cols = kernel_cols + (kernel_cols - 1) * dilation
    if isinstance(pad, str) and pad.lower() == 'same':
        pr_total = max(0, (in_rows - 1) * stride + effective_kernel_rows - in_rows)
        pc_total = max(0, (in_cols - 1) * stride + effective_kernel_cols - in_cols)
        pr1 = pr_total // 2
        pr2 = pr_total - pr1
        pc1 = pc_total // 2
        pc2 = pc_total - pc1
        pad = (pr1, pr2, pc1, pc2)
    elif isinstance(pad, int):
        pad = (pad, pad, pad, pad)
    elif isinstance(pad, (tuple, list)):
        if len(pad) == 2:
            (pr, pc) = pad
            pad = (pr, pr, pc, pc)
        elif len(pad) == 4:
            pass
        else:
            pad = (0, 0, 0, 0)
    else:
        pad = (0, 0, 0, 0)
    if any((p > 0 for p in pad)):
        X_padded = np.pad(X, ((0, 0), (pad[0], pad[1]), (pad[2], pad[3]), (0, 0)), mode='constant')
    else:
        X_padded = X
    out_rows = (in_rows + pad[0] + pad[1] - effective_kernel_rows) // stride + 1
    out_cols = (in_cols + pad[2] + pad[3] - effective_kernel_cols) // stride + 1
    im2col_matrix = np.zeros((n_ex, kernel_rows * kernel_cols * in_ch, out_rows * out_cols))
    for i in range(out_rows):
        for j in range(out_cols):
            patch = np.zeros((n_ex, kernel_rows, kernel_cols, in_ch))
            for kr in range(kernel_rows):
                for kc in range(kernel_cols):
                    h_offset = i * stride + kr * (1 + dilation)
                    w_offset = j * stride + kc * (1 + dilation)
                    if h_offset < X_padded.shape[1] and w_offset < X_padded.shape[2]:
                        patch[:, kr, kc, :] = X_padded[:, h_offset, w_offset, :]
            im2col_matrix[:, :, i * out_cols + j] = patch.reshape(n_ex, -1)
    W_reshaped = W.reshape(-1, out_ch)
    result = np.matmul(im2col_matrix.transpose(0, 2, 1), W_reshaped)
    result = result.reshape(n_ex, out_rows, out_cols, out_ch)
    return result.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space_continuity(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, (Tuple, Dict)):
            subspaces = space.spaces
            if isinstance(space, Tuple):
                return all((check_space_continuity(sub) for sub in subspaces))
            elif isinstance(space, Dict):
                return all((check_space_continuity(sub) for sub in subspaces.values()))
        else:
            return False
    cont_action = False
    if tuple_action:
        cont_action = check_space_continuity(env.action_space)
    else:
        cont_action = isinstance(env.action_space, Box)
    cont_obs = False
    if tuple_obs:
        cont_obs = check_space_continuity(env.observation_space)
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

