def is_symmetric(X: list[list[int | float]]) -> bool:
    """Check whether the given 2-D array *X* is symmetric.

    A matrix is symmetric if it is square and equal to its own transpose.

    Args:
        X: Matrix represented as a list of lists containing numbers.

    Returns:
        True if the matrix is symmetric, otherwise False.
    """
    try:
        matrix = np.array(X)
    except:
        return False
    if matrix.shape[0] != matrix.shape[1]:
        return False
    return np.allclose(matrix, matrix.T)

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.column_stack([np.ones(X.shape[0]), X])
        X_new = np.column_stack([np.ones(X_new.shape[0]), X_new])
    (N, M) = X.shape
    if isinstance(mu, (float, int)):
        mu = np.full(M, mu)
    elif isinstance(mu, np.ndarray) and mu.ndim == 0:
        mu = np.full(M, mu.item())
    elif isinstance(mu, np.ndarray) and mu.ndim == 1:
        if len(mu) != M:
            raise ValueError(f'mu must have length {M} when fit_intercept is {fit_intercept}')
    else:
        raise ValueError('mu must be a scalar or a 1-D array')
    if V is None:
        V = np.eye(M)
    elif isinstance(V, (float, int)):
        V = V * np.eye(M)
    elif isinstance(V, np.ndarray):
        if V.ndim == 0:
            V = V.item() * np.eye(M)
        elif V.ndim == 1:
            if len(V) != M:
                raise ValueError(f'V as 1-D array must have length {M}')
            V = np.diag(V)
        elif V.ndim == 2:
            if V.shape != (M, M):
                raise ValueError(f'V as 2-D array must be of shape ({M}, {M})')
        else:
            raise ValueError('V must be a scalar, 1-D, or 2-D array')
    else:
        raise ValueError('V must be a scalar, 1-D array, 2-D array, or None')
    V_inv = np.linalg.inv(V)
    XT_X = X.T @ X
    Sigma_p = np.linalg.inv(V_inv + XT_X)
    mu_p = Sigma_p @ (V_inv @ mu + X.T @ y)
    y_pred = X_new @ mu_p
    y_pred_rounded = [round(val, 4) for val in y_pred]
    return y_pred_rounded

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error between actual and predicted values."""
    if len(actual) != len(predicted):
        return -1.0
    if any((a < 0 for a in actual)) or any((p < 0 for p in predicted)):
        return -1.0
    sum_squared_log_errors = 0.0
    n = len(actual)
    for (a, p) in zip(actual, predicted):
        log_diff = np.log1p(p) - np.log1p(a)
        sum_squared_log_errors += log_diff ** 2
    rmsle = np.sqrt(sum_squared_log_errors / n)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x_array = np.asarray(x)
    sigma = 1 / (1 + np.exp(-x_array))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    rounded_result = np.round(result, 4)
    if x_array.shape == ():
        return float(rounded_result)
    else:
        return rounded_result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not transactions or min_sup <= 0 or min_sup > 1:
        return []
    total_transactions = len(transactions)
    min_support_count = min_sup * total_transactions
    frequent_itemsets = []
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    frequent_k = [tuple([item]) for item in item_counts if item_counts[item] >= min_support_count]
    frequent_k.sort()
    frequent_itemsets.extend(frequent_k)
    k = 2
    while frequent_k:
        candidates = set()
        for i in range(len(frequent_k)):
            for j in range(i + 1, len(frequent_k)):
                itemset1 = frequent_k[i]
                itemset2 = frequent_k[j]
                if itemset1[:-1] == itemset2[:-1]:
                    new_candidate = tuple(sorted(itemset1 + (itemset2[-1],)))
                    candidates.add(new_candidate)
        pruned_candidates = []
        for candidate in candidates:
            subsets = list(itertools.combinations(candidate, k - 1))
            all_subsets_frequent = True
            for subset in subsets:
                if subset not in frequent_k:
                    all_subsets_frequent = False
                    break
            if all_subsets_frequent:
                pruned_candidates.append(candidate)
        candidate_counts = {}
        for candidate in pruned_candidates:
            candidate_counts[candidate] = 0
        for transaction in transactions:
            transaction_set = set(transaction)
            for candidate in pruned_candidates:
                if set(candidate).issubset(transaction_set):
                    candidate_counts[candidate] += 1
        frequent_k = [tuple(sorted(candidate)) for candidate in candidate_counts if candidate_counts[candidate] >= min_support_count]
        frequent_k.sort()
        frequent_itemsets.extend(frequent_k)
        k += 1
    return frequent_itemsets

import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Cluster *data* with the DBSCAN algorithm.

    Parameters
    ----------
    data : np.ndarray
        Two-dimensional array where each row is a sample and columns are
        features.
    eps : float
        Maximum radius of the neighborhood to be considered reachable.
    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if len(data) == 0:
        return []
    n_samples = data.shape[0]
    labels = [-1] * n_samples
    cluster_id = 0
    for i in range(n_samples):
        if labels[i] != -1:
            continue
        neighbors = []
        for j in range(n_samples):
            if np.linalg.norm(data[i] - data[j]) <= eps:
                neighbors.append(j)
        if len(neighbors) < min_samples:
            continue
        else:
            labels[i] = cluster_id
            seed_set = neighbors.copy()
            seed_set.remove(i)
            while seed_set:
                j = seed_set.pop(0)
                if labels[j] == -1:
                    labels[j] = cluster_id
                    j_neighbors = []
                    for k in range(n_samples):
                        if np.linalg.norm(data[j] - data[k]) <= eps:
                            j_neighbors.append(k)
                    if len(j_neighbors) >= min_samples:
                        for k in j_neighbors:
                            if labels[k] == -1 and k not in seed_set:
                                seed_set.append(k)
                elif labels[j] == -1:
                    labels[j] = cluster_id
            cluster_id += 1
    return labels

import numpy as np
from itertools import combinations_with_replacement, product
def polynomial_features(X: list[list[int | float]], degree: int) -> list[list[float]]:
    """Generate a full polynomial feature matrix up to a given degree."""
    if degree < 0:
        return -1
    X_array = np.array(X, dtype=float)
    (n_samples, n_features) = X_array.shape
    features = []
    for d in range(degree + 1):
        for indices in combinations_with_replacement(range(n_features), d):
            exponents = np.zeros(n_features, dtype=int)
            for idx in indices:
                exponents[idx] += 1
            features.append(exponents)
    result = []
    for sample in X_array:
        row = []
        for exp in features:
            term = 1.0
            for f in range(n_features):
                term *= sample[f] ** exp[f]
            row.append(term)
        result.append(row)
    return result

import numpy as np
from typing import Callable
def unhot(function: Callable) -> Callable:
    """Decorator that converts one-hot encoded label arrays to 1-D class labels.

    If either *actual* or *predicted* is a 2-D array whose second dimension is
    larger than one, the array is assumed to be one-hot encoded and is replaced
    by its ``argmax`` along axis 1 before the wrapped *function* is executed.

    Args:
        function: A metric function accepting two NumPy arrays ``(actual,
            predicted)`` and returning a value of any type.

    Returns:
        Callable: A new function that performs the described conversion and then
        calls *function*.
    """

    def wrapper(actual: np.ndarray, predicted: np.ndarray) -> float:
        if actual.ndim == 2 and actual.shape[1] > 1:
            actual_processed = actual.argmax(axis=1)
        else:
            actual_processed = actual.reshape(-1) if actual.ndim > 1 else actual
        if predicted.ndim == 2 and predicted.shape[1] > 1:
            predicted_processed = predicted.argmax(axis=1)
        else:
            predicted_processed = predicted.reshape(-1) if predicted.ndim > 1 else predicted
        return function(actual_processed, predicted_processed)
    return wrapper

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (list, np.ndarray)):
        if isinstance(z, list):
            if not z:
                return []
            processed = []
            for item in z:
                processed.append(sigmoid_grad(item))
            return processed
        else:
            grad = z * (1 - z)
            rounded_grad = np.round(grad, 4)
            return rounded_grad
    elif isinstance(z, (float, int)):
        grad = z * (1 - z)
        return round(grad, 4)
    else:
        raise TypeError('Input must be a scalar, list, or numpy array')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while True:
        if hasattr(current_node, 'y'):
            return current_node.y
        feature_index = current_node.label
        feature_value = features[feature_index]
        if task_type == 'classification':
            matched_child = None
            for child in current_node.child:
                if hasattr(child, 'x') and child.x == feature_value:
                    matched_child = child
                    break
            if matched_child is None:
                current_node = current_node.child[1]
            else:
                current_node = matched_child
        elif task_type == 'regression':
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
        else:
            raise ValueError('Invalid task_type')

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    unique_labels = np.unique(y_train)
    num_features = X_train.shape[1]
    feature_map = {}
    feature_id = 0
    feature_values = defaultdict(set)
    for feature_idx in range(num_features):
        feature_values[feature_idx] = set(X_train[:, feature_idx])
    for feature_idx in range(num_features):
        for feature_val in feature_values[feature_idx]:
            for label in unique_labels:
                feature_map[feature_idx, feature_val, label] = feature_id
                feature_id += 1
    num_features_total = feature_id
    empirical_counts = np.zeros(num_features_total)
    total_samples = len(X_train)
    for (x, y) in zip(X_train, y_train):
        for feature_idx in range(num_features):
            feature_val = x[feature_idx]
            key = (feature_idx, feature_val, y)
            if key in feature_map:
                empirical_counts[feature_map[key]] += 1
    empirical_expectation = empirical_counts / total_samples
    weights = np.zeros(num_features_total)
    C = num_features
    for _ in range(n_iter):
        model_expectation = np.zeros(num_features_total)
        for x in X_train:
            Z_x = 0.0
            log_probs = []
            for label in unique_labels:
                sum_wf = 0.0
                for feature_idx in range(num_features):
                    feature_val = x[feature_idx]
                    key = (feature_idx, feature_val, label)
                    if key in feature_map:
                        sum_wf += weights[feature_map[key]]
                log_probs.append(sum_wf)
            max_log = max(log_probs) if log_probs else 0.0
            log_probs = [lp - max_log for lp in log_probs]
            Z_x = sum((math.exp(lp) for lp in log_probs))
            log_Z_x = math.log(Z_x) + max_log if Z_x != 0 else 0.0
            for (i, label) in enumerate(unique_labels):
                sum_wf = 0.0
                active_features = []
                for feature_idx in range(num_features):
                    feature_val = x[feature_idx]
                    key = (feature_idx, feature_val, label)
                    if key in feature_map:
                        sum_wf += weights[feature_map[key]]
                        active_features.append(feature_map[key])
                log_p_y_x = sum_wf - log_Z_x
                p_y_x = math.exp(log_p_y_x)
                for j in active_features:
                    model_expectation[j] += p_y_x / total_samples
        max_diff = 0.0
        for j in range(num_features_total):
            if empirical_expectation[j] == 0:
                continue
            if model_expectation[j] == 0:
                delta = 0.0
            else:
                delta = 1.0 / C * math.log(empirical_expectation[j] / model_expectation[j])
            weights[j] += delta
            max_diff = max(max_diff, abs(delta))
        if max_diff < epsilon:
            break
    predictions = []
    for x in X_test:
        best_label = None
        max_p = -float('inf')
        for label in unique_labels:
            sum_wf = 0.0
            for feature_idx in range(num_features):
                feature_val = x[feature_idx]
                key = (feature_idx, feature_val, label)
                if key in feature_map:
                    sum_wf += weights[feature_map[key]]
            if sum_wf > max_p:
                max_p = sum_wf
                best_label = label
        predictions.append(best_label)
    return predictions

from collections import defaultdict
from typing import Iterable, Tuple, List, Set, Any
def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    """Calculate how often each word appears across documents.

    The function returns a list with the document-frequency ratio of every word
    (sorted from highest to lowest) and a set containing only the *cut_off*
    most frequent words. When *cut_off* is ``None`` all words are placed in the
    set. Every ratio must be rounded to four decimal places.

    Args:
        data: Iterable of documents. Each document is represented by a tuple
              ``(label, words)`` where *label* is ignored and *words* is an
              iterable of strings.
        cut_off: Number of top words to include in the returned set, or
                  ``None`` to include every word.

    Returns:
        Tuple with (1) a list of (word, ratio) pairs and (2) a set of the most
        frequent words as described above.
    """
    if not data:
        return ([], set())
    word_doc_counts = defaultdict(int)
    total_docs = len(data)
    for (_, words) in data:
        unique_words_in_doc = set(words)
        for word in unique_words_in_doc:
            word_doc_counts[word] += 1
    word_ratios = [(word, round(count / total_docs, 4)) for (word, count) in word_doc_counts.items()]
    word_ratios_sorted = sorted(word_ratios, key=lambda x: (-x[1], x[0]))
    if cut_off is None:
        top_words = {word for (word, _) in word_ratios_sorted}
    elif cut_off <= 0:
        top_words = set()
    else:
        top_words = {word for (word, _) in word_ratios_sorted[:cut_off]}
    return (word_ratios_sorted, top_words)

import numpy as np
def kmeans_centroids(X: 'np.ndarray', k: int, max_iters: int=100) -> list[list[float]]:
    """Implement K-Means clustering to compute centroids only."""
    centroids = X[:k].copy()
    for _ in range(max_iters):
        distances = np.sum((X[:, np.newaxis] - centroids) ** 2, axis=2)
        labels = np.argmin(distances, axis=1)
        new_centroids = np.zeros_like(centroids)
        for i in range(k):
            cluster_samples = X[labels == i]
            if len(cluster_samples) > 0:
                new_centroids[i] = cluster_samples.mean(axis=0)
            else:
                new_centroids[i] = centroids[i]
        if np.max(np.abs(new_centroids - centroids)) < 0.0001:
            break
        centroids = new_centroids
    sorted_centroids = sorted(centroids.tolist())
    rounded_centroids = [[round(coord, 4) for coord in centroid] for centroid in sorted_centroids]
    return rounded_centroids
