def feature_scaling(data: np.ndarray) -> (list[list[float]], list[list[float]]):
    standardized_data = (data - np.mean(data, axis=0)) / np.std(data, axis=0)
    standardized_list = standardized_data.tolist()
    min_vals = np.min(data, axis=0)
    max_vals = np.max(data, axis=0)
    min_max_normalized_data = (data - min_vals) / (max_vals - min_vals)
    min_max_normalized_list = min_max_normalized_data.tolist()
    for sublist in standardized_list:
        for i in range(len(sublist)):
            sublist[i] = round(sublist[i], 4)
    for sublist in min_max_normalized_list:
        for i in range(len(sublist)):
            sublist[i] = round(sublist[i], 4)
    return (standardized_list, min_max_normalized_list)
