from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    if not examples:
        return most_common_value([example[target_attr] for example in examples])
    elif all_same_class(examples, target_attr):
        return examples[0][target_attr]
    elif not attributes:
        return most_common_value([example[target_attr] for example in examples])
    else:
        best_attr = choose_best_attribute(examples, attributes, target_attr)
        tree = {best_attr: {}}
        for value in unique_values(examples, best_attr):
            subtree_examples = [example for example in examples if example[best_attr] == value]
            subtree_attributes = [attr for attr in attributes if attr != best_attr]
            subtree = learn_decision_tree(subtree_examples, subtree_attributes, target_attr)
            tree[best_attr][value] = subtree
        return tree
def entropy(examples: list[dict], target_attr: str) -> float:
    class_counts = Counter([example[target_attr] for example in examples])
    total_count = len(examples)
    entropy_value = 0
    for count in class_counts.values():
        p = count / total_count
        entropy_value -= p * math.log2(p)
    return entropy_value
def information_gain(examples: list[dict], attribute: str, target_attr: str) -> float:
    parent_entropy = entropy(examples, target_attr)
    total_count = len(examples)
    weighted_entropy = 0
    for value in unique_values(examples, attribute):
        subset = [example for example in examples if example[attribute] == value]
        subset_count = len(subset)
        weighted_entropy += subset_count / total_count * entropy(subset, target_attr)
    return parent_entropy - weighted_entropy
def choose_best_attribute(examples: list[dict], attributes: list[str], target_attr: str) -> str:
    best_gain = 0
    best_attr = None
    for attr in attributes:
        gain = information_gain(examples, attr, target_attr)
        if gain > best_gain:
            best_gain = gain
            best_attr = attr
    return best_attr
def unique_values(examples: list[dict], attribute: str) -> list:
    return list(set([example[attribute] for example in examples]))
def all_same_class(examples: list[dict], target_attr: str) -> bool:
    first_class = examples[0][target_attr]
    return all((example[target_attr] == first_class for example in examples))
def most_common_value(class_list: list) -> str:
    counter = Counter(class_list)
    return counter.most_common(1)[0][0]
