def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    b = 0
    if kernel == 'linear':
        kernel_matrix = np.dot(data, data.T)
    elif kernel == 'rbf':
        kernel_matrix = np.exp(-np.linalg.norm(data[:, np.newaxis] - data[np.newaxis, :], axis=2) ** 2 / (2 * sigma ** 2))
    else:
        raise ValueError('Unsupported kernel')
    for _ in range(iterations):
        for i in range(n_samples):
            margin = labels[i] * (np.dot(alphas * labels, kernel_matrix[i]) + b)
            if margin < 1:
                alphas[i] = (1 - lambda_val * 1 / n_samples) * alphas[i] + lambda_val * labels[i]
                b = b + labels[i]
            else:
                alphas[i] = (1 - lambda_val * 1 / n_samples) * alphas[i]
    return (alphas.reshape(-1, 1).tolist(), round(b, 4))
