def rref(matrix):
    m = np.array(matrix, dtype=float)
    (rows, cols) = m.shape
    pivot_row = 0
    for col in range(cols):
        if pivot_row >= rows:
            break
        non_zero_row = pivot_row
        while non_zero_row < rows and m[non_zero_row, col] == 0:
            non_zero_row += 1
        if non_zero_row == rows:
            continue
        m[[pivot_row, non_zero_row]] = m[[non_zero_row, pivot_row]]
        pivot = m[pivot_row, col]
        m[pivot_row] = m[pivot_row] / pivot
        for i in range(rows):
            if i != pivot_row:
                factor = m[i, col]
                m[i] = m[i] - factor * m[pivot_row]
        pivot_row += 1
    for i in range(rows - 1, 0, -1):
        for j in range(i):
            factor = m[j, i]
            m[j] = m[j] - factor * m[i]
    return m.tolist()
