def gauss_seidel(A, b, n, x_ini=None):
    if x_ini is None:
        x = np.zeros(len(b))
    else:
        x = x_ini.copy()
    for _ in range(n):
        for i in range(len(A)):
            s1 = np.dot(A[i, :i], x[:i])
            s2 = np.dot(A[i, i + 1:], x[i + 1:])
            x[i] = (b[i] - s1 - s2) / A[i, i]
    return np.round(x, 4).tolist()
