def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('Corpus cannot be empty')
    num_docs = len(corpus)
    tf = []
    for doc in corpus:
        doc_tf = {}
        total_words = len(doc)
        for word in doc:
            doc_tf[word] = doc_tf.get(word, 0) + 1
        for word in doc_tf:
            doc_tf[word] = doc_tf[word] / total_words
        tf.append(doc_tf)
    df = {}
    for doc in corpus:
        unique_words = set(doc)
        for word in unique_words:
            df[word] = df.get(word, 0) + 1
    idf = {}
    for word in df:
        idf[word] = np.log((num_docs + 1) / (df[word] + 1)) + 1
    query_tf = {}
    total_query_words = len(query)
    for word in query:
        query_tf[word] = query_tf.get(word, 0) + 1
    for word in query_tf:
        query_tf[word] = query_tf[word] / total_query_words
    tf_idf_scores = []
    for doc_tf in tf:
        doc_tf_idf = []
        for word in query:
            tf_score = doc_tf.get(word, 0)
            idf_score = idf.get(word, 0)
            doc_tf_idf.append(tf_score * idf_score)
        tf_idf_scores.append(doc_tf_idf)
    rounded_scores = np.round(tf_idf_scores, 5).tolist()
    return rounded_scores
